<script>
var cpt_gnb = Vue.component('cpt_gnb', {
    template: '#cpt_gnb_tem',
    name: 'cpt_gnb',
    data(){
        return {
        	user_menu: false,
            gnb_keyword:''
        }
    },
    watch:{
        search_clear(to){
            if(to){
                this.gnb_keyword = '';
                this.$store.commit('search_clear',false);
            }
        },
        login_user_info(){
            //console.log('login_user_info',this.login_user_info);
        }
    },
    props:['login_user_info','cart_count','my_channel_data'],
    computed:{
        search_clear(){
            return this.$store.getters.search_clear;
        },
        isLogin(){
            return this.login_user_info.no > 0;
        },
        gnb_keyword(to){}
    },
    methods:{
        logout(){
	        var chkConfirm = confirm('정말 로그아웃 하시겠습니까?');

	        if (chkConfirm) {
		        this.$emit('run_logout')
	        }
        },
        search(){
            if(this.gnb_keyword){
                this.$router.push({
                    path:'/search',
                    query:{
                        keyword:this.gnb_keyword
                    }
                })
            }
        },
        go_basket(){
            location.href = '/order/basket.html';
        }
    },
	mounted(){
        if(this.$route.query.keyword){
            this.gnb_keyword = this.$route.query.keyword;
        }
	},
	destroyed() {
		this.user_menu = null;
		this.gnb_keyword = null;
	}
})
</script>


