<script>
var cpt_lnb = Vue.component('cpt_lnb', {
    template: '#cpt_lnb_tem',
    name: 'cpt_lnb',
    data(){
        return {
            menus: [],
            channels:[],
            total:0,
            channel_more:false,
            last_path:'/',

            row_count:5,
            init_load:false,
            no_my_channel:false,
            channel_row_count : 5,
        }
    },
    props:['lnb_mini','path','login_user_info'],
    computed:{
        channel_title(){
            if(this.isLogin && !this.no_my_channel){
                return '구독';
            }else{
                return '추천';
            }
        },
        api_url(){
             if(!this.isLogin){
                return 'https://eatalk.live24.app/api/channel/json/list';
             }else{
                 return 'https://eatalk.live24.app/api/channel/json/my_list';
             }
        },
        isLogin(){
            return this.login_user_info.no > 0;
        },
        user_key(){
            return this.login_user_info.user_key;
        }


    },
    watch:{
        isLogin(){
            this.init_run();
        },
        last_path(to){
           //console.log('lll',to,this.$route.path)
        },
        $route(to, from) {
            if(localStorage.getItem('last_path')){
                this.last_path = localStorage.getItem('last_path');
            }else{
                this.last_path = this.$route.path;
            }
        }
    },
    methods:{
        save_link(isf){
            this.last_path = isf;
            localStorage.setItem('last_path',isf);
        },
        change_channel_more:_.debounce(function (){
            this.channel_more = !this.channel_more;
            if(this.channel_more){
                this.channel_row_count = 300;
            }else{
                this.channel_row_count = 5;
            }
            this.init_run();
        },300),
        get_my_channel_list(timer){
            var vm = this;
            var params =[];
            if(!timer) timer = 200;


            vm.channels = [];
            vm.init_load = false;
            vm.no_my_channel = false;
            vm.total = 0;

            if(!vm.user_key){

                vm.get_recom_channel_list();
                return;
            }

            var res = get_cache('cpt_lnb_'+vm.user_key+'_'+vm.channel_row_count);
            if(res){
                vm.channels = res.result.data;
                vm.total = res.result.total;
                if(vm.total <1){
                    vm.no_my_channel = true;
                    vm.get_recom_channel_list();
                }else{
                    setTimeout(()=>{
                        vm.init_load = true;
                    },timer);
                }
                return;
            }

            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/channel/json/my_list',
                data: {
                    row_count:vm.channel_row_count,
                    order_col:'no',
                    order_type:'desc',
                    key:vm.user_key
                },
                dataType: 'json',
            	success(res, status) {
            		if(res.code==200){

                        vm.channels = res.result.data;
                        vm.total = res.result.total;

                        set_cache('cpt_lnb_'+vm.user_key+'_'+vm.channel_row_count,res,600);

                        if(vm.total <1){
                            vm.no_my_channel = true;
                            vm.get_recom_channel_list();
                        }else{
                            setTimeout(()=>{
                                vm.init_load = true;
                            },timer);
                        }

            		}

            	},
            	error(request,status,error) {
            		console.log(request.responseText);
                    vm.init_load = true;
            	}
            });
        },
        get_recom_channel_list(){
            var vm = this;
            var params =[];

            vm.total = 0;


            var res = get_cache('cpt_lnb_recom'+vm.channel_row_count);
            if(res){
                vm.channels = res.result.data;
                vm.total = res.result.total;
                vm.init_load = true;
                return;
            }

            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/channel/json/recom_list',
                data: {
                    row_count:vm.channel_row_count,
                    sch_is_recom:'Y',
                    order_col:'ranking',
                    order_type:'asc',
                },
                dataType: 'json',
            	success(res, status) {
            		if(res.code==200){

                        vm.channels = res.result.data;
                        vm.total = res.result.total;

                        set_cache('cpt_lnb_recom'+vm.channel_row_count,res,600);

            		}
                    vm.init_load = true;
            	},
            	error(request,status,error) {
            		console.log(request.responseText);
            	}
            });
        },
        init_run(){
            if(this.isLogin && !this.no_my_channel){
                this.get_my_channel_list();
            }else{
                this.get_recom_channel_list();
            }

            if(this.isLogin){
                this.menus = [
                    { type:'link', title: '홈', link: '/feed/home', icon:'ph-house-line' },
                    { type:'link', title: '내 채널', link: '/channel/detail/'+this.login_user_info.channel_no, icon:'ph-user-rectangle' },
                    { type:'link', title: '탐색', link: '/feed/exp', icon:'ph-compass' },
                    { type:'link', title: '구독한영상', link: '/feed/subscribe', icon:'ph-play-circle' },
                    { type:'link', title: '포스트', link: '/feed/post', icon:'ph-note' },
                    { type:'link', title: '시청기록', link: '/feed/history', icon:'ph-monitor-play' },
                    { type:'link', title: '마이페이지', link: '/feed/mypage', icon:'ph-user' },
                    { type:'line',  },
                    { type:'subscribe', title:'구독채널'  },
                    { type:'line',  },
                ]
            }else{
                this.menus = [
                    { type:'link', title: '홈', link: '/feed/home', icon:'ph-house-line' },
                    { type:'link', title: '탐색', link: '/feed/exp', icon:'ph-compass' },
                    { type:'link', title: '구독한영상', link: '/feed/subscribe', icon:'ph-play-circle' },
                    { type:'link', title: '포스트', link: '/feed/post', icon:'ph-note' },
                    { type:'link', title: '시청기록', link: '/feed/history', icon:'ph-monitor-play' },
                    { type:'link', title: '마이페이지', link: '/feed/mypage', icon:'ph-user' },
                    { type:'line',  },
                    { type:'subscribe', title:'추천채널'  },
                    { type:'line',  },
                ]
            }
        }

    },
    created(){
        if(localStorage.getItem('last_path')){
            this.last_path = localStorage.getItem('last_path');
        }else{
            this.last_path = this.$route.path;
        }
        this.init_run();

    }
})
</script>

