<script>
var cpt_background = Vue.component('cpt_background', {
    template: '#cpt_background_tem',
    name: 'cpt_background',
    data(){
        return {
            init_load : false,
            bgdatas:[],
	        bgdatas_url: '',
            slide_idx:0,
            row_count:5,
            total:0,
	        sliderTimer: 5000,
            vheight:0,
            is_left:true,
            width:0,
        }
    },

    computed:{
        is_bgdata(){
            return this.bgdatas[this.slide_idx];
        },
        is_mobile(){
            return /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
        },
    },
    watch:{
        slide_idx(){
            this.progressBar();
            $('.slide_text_area_ani').hide();
            setTimeout(()=>{
                $('.slide_text_area_ani').show();
            },100);

        },
        width(){
            //this.resize_bs();
        }
    },
    methods: {
       get_bs_data() {
            return new Promise((resolve, reject) => {
                $.ajax({
                    type: 'GET',
                    url: 'https://eatalk.live24.app/api/content/json/list',
                    data:{
                        row_count:this.row_count,
                    },
                    dataType: 'json',
                	success: (res, status) => {
                		this.bgdatas = res.result.data;
                        this.total = res.result.total;
                        resolve();
                	},
                	error: function(request,status,error) {
                		console.log(request.responseText);
                        reject(error);
                	}
                });
            });
        },
        prev(){
            $('#visual_slider').slick('prev');
        },
        next(){
            $('#visual_slider').slick('next');
        },
	    progressBar() {
            $('.slider-progress > div').removeAttr('style');
            $('.slider-progress > div').width(0);
            $('.slider-progress > div').css('transition-duration', this.sliderTimer / 1000 + 's').width('100%');

	    },
    	set_slider() {
		    var $slider = $('#visual_slider');

            if ($slider.length == 0) {
                // no slider element
                return;
            }

		    var options = {
			    lazyLoad: 'ondemand',
			    autoplay: true,
			    autoplaySpeed: this.sliderTimer,
			    infinite: true,
			    dots: false,
			    draggable: true,
			    arrows: false,
			    adaptiveHeight: true,
			    nextArrow: $('.next'),
			    prevArrow: $('.prev'),
		    }
		    
		    $slider.slick(options).on('beforeChange', function (event, slick, afterChange, nextSlide) {

		    }).on('afterChange', (event, slick, currentSlide) => {
                this.slide_idx = currentSlide;
		    });

            setTimeout(() => {
                this.init_load = true;
            }, 1000);

            this.progressBar();
	    },

        resize_bs:_.debounce(function(){
            if(this.width >= 762){
                this.get_bs_data().then(() => {
                    setTimeout(() => { this.set_slider(); }, 1000);
                });
            }
        },1200),

        window_resize_handler: _.throttle(() => {
            this.width = $(window).width();
        }, 300),

	},

    mounted() {

        this.width = $(window).width();

        window.addEventListener('resize', this.window_resize_handler, false);

        this.get_bs_data().then(() => {
            setTimeout(() => {
                this.set_slider();
                }, 1000);
        });
	},

    created() {
        // console.log('[DEBUG] cpt_background created.');
    },

    destroyed() {
        this.bgdatas = null;
        this.total = null;
        window.removeEventListener('resize', this.window_resize_handler, false);
        // console.log('[DEBUG] cpt_background destroyed.');
    }
})
</script>

