<script>
var cpt_video_product = Vue.component('cpt_video_product', {
	template: '#cpt_video_product_tem',
	props: ['product_info','login_user_info'],
    data(){
        return{
            show_product:false,
            s_frame:false,
            mode:'detail'
        }
    },
    computed:{
        sale(){
            if( this.product_info.retail_price == 0){

            }else if(this.product_info.price == this.product_info.retail_price){

            }else{
                return 100 - this.round(   this.product_info.price / this.product_info.retail_price * 100)
            }
        },
        to_price(){
            return this.round(this.product_info.price);
        },
        from_price(){
            return this.round(this.product_info.retail_price);
        },
        purl(){
            var inflow_id='';
            if(navigator.userAgent.indexOf('EatalkApp') > -1){
                inflow_id = 'eatalkapp';
            }

            return '/product/detail.html?product_no='+this.product_info.product_no+'&ghost_mall_id='+inflow_id+'&ifrmode=true'
        },
        isLogin(){
            return this.login_user_info.no > 0;
        },
    },
    methods:{
        round(val){

            return Math.round(val * Math.pow(10, 0 ))/Math.pow(10, 0);
        },
        view_product(mode){
            var vm = this;
            this.mode = mode;
	        this.$store.commit('popup_state', true)

            player.pause();

            this.show_product = true;
            setTimeout(()=>{
                this.s_frame = true;
            },200);
        },
        close_product(){
            var vm = this;
	        this.s_frame = false;
            player.play();

            vm.$emit('cart_cnt_sync');

            setTimeout(()=>{
                this.show_product = false;
            },200);
        },
    },
    created() {
        if(this.$route.query.product_open){
            this.view_product('detail');
        }
    }
})
</script>

