<script>
var cpt_video_card = Vue.component('cpt_video_card', {
    template: '#cpt_video_card_tem',
    name: 'cpt_video_card',
    data(){
        return {
            mouseover:false,
            is_offset:0,
            is_play_show:false,
            mouseover_show:false,
            otimer:'',
            utimer:'',
            inapp:mobileAgent,
            is_scroll:false,
            is_scroll_timer:'',
            is_play_timer:'',
            init_load_single:false,
            xscroll_y:false,
            is_left_offset:0,
            width:0,
            is_debug:false,
            is_loading_end:false,
            url: null
        }
    },
    props:['init_load','vod_list','replace','is_mobile','window_scroll','is_popup_run','is_scroll_up_down','no_video_thumb','xscroll','xscroll_play'],
    computed:{
        thumb_url_ts(){
            if( this.is_loading_end ){
                return this.vod_list.thumb_url
            }else{
                return this.vod_list.thumb_small_url
            }
        },
        channel_img(){
            if(this.vod_list.channel.channel_profile_img){
                return this.vod_list.channel.channel_profile_img;
            }else{
                return this.vod_list.user.profile_img_url;
            }
        },
        ts_ids(){
            if(this.xscroll_play){
                return 'ts_'+ts_no;
            }
        },
        paws(){
            if(this.xscroll > (this.width -  ($(window).width() - this.width) )){
                if(( this.xscroll / this.width ) < 1){
                    return  ($(window).width() - this.width);
                }else{
                    return (this.xscroll / this.width) * (($(window).width() - this.width) + 15);
                }
            }else{
                return 10;
            }
        },
        window_scroll_to(){
            if(this.xscroll_play){
                if(this.is_scroll_up_down == 'up'){
                    return parseInt(this.window_scroll + $('.header_wrap').height() + 200);
                }else{
                    return parseInt(this.window_scroll + $('.header_wrap').height() + 160);
                }
            }else{
                if(this.is_scroll_up_down == 'up'){
                    return parseInt(this.window_scroll + $('.header_wrap').height() + 160);
                }else{
                    return parseInt(this.window_scroll + $('.header_wrap').height() + 100);
                }
            }
        },
        xw_scroll_left(){
            return (this.is_left_offset + this.width) - 20;
        },
        is_play(){
            if(this.xscroll_play){
                if(this.is_offset <= this.window_scroll_to && (this.is_offset + $('.vvont').height() ) >= this.window_scroll_to && $(window).width() < 800 && !this.is_scroll && this.xscroll + this.paws >= (this.is_left_offset) && this.xscroll + this.paws <= this.xw_scroll_left){
                    if(!this.is_popup_run){
                        if(!this.no_video_thumb){
                            return true;
                        }
                    }
                }else{
                    return false;
                }
            }else{
                if(this.is_offset <= this.window_scroll_to && (this.is_offset + $('.vvont').height() ) >= this.window_scroll_to && $(window).width() < 800 && !this.is_scroll){
                    if(!this.is_popup_run){
                        if(!this.no_video_thumb){
                            return true;
                        }
                    }
                }else{
                    return false;
                }
            }
        },
        isInApp(){
            if(window.userAgent.indexOf('EatalkApp') > -1){
                return true;
            }
        },
        product_data(){
            if(this.vod_list.product_info){
                return this.vod_list.product_info;
            }
        },
        purl(){
            var inflow_id='';
            if(navigator.userAgent.indexOf('EatalkApp') > -1){
                inflow_id = 'eatalkapp';
            }
            return '/product/detail.html?product_no='+this.vod_list.product_info.product_no+'&ghost_mall_id='+inflow_id+'&ifrmode=true'
        },
    },
    watch: {
        xscroll(to){
            if(!this.is_scroll){
                this.is_scroll = true;
                clearTimeout(this.is_scroll_timer);
                this.is_scroll_timer = setTimeout(() => {
                    this.is_scroll = false;
                    this.is_play_show = false;
                }, 500);
            }
        },
        window_scroll(){
            if(!this.is_scroll){
                this.is_scroll = true;
                clearTimeout(this.is_scroll_timer);
                this.is_scroll_timer = setTimeout(() => {
                    this.is_scroll = false;
                    this.is_play_show = false;
                }, 500);
            }
        },
        is_play(to){
            clearTimeout(this.is_play_timer);
            this.is_play_timer = setTimeout(()=>{
                this.is_play_show = this.is_play;
            },500);
        },
        is_popup_run(to){
            this.is_offset = $('#thumb_'+this.vod_list.no).offset().top;
        },
        is_scroll(to){},
    },
    methods:{
        close_popup() {
            this.url = null;
        },
        open_detail() {
            this.show_detail = true;
            this.url = this.purl;
        },
        open_product(){
            // console.log('this.product_data',this.product_data);
            if(this.product_data){
                if (/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)) {
                    console.log('dddddddd')
                    this.$router.push({path:this.$route.path, query:{popup_url:this.purl,popup_title:'상품보기'}});
                } else {
                    this.open_detail();
                }
            }
        },
        is_xscroll_v:_.debounce(function(){
            if($('#thumb_' + this.vod_list.no).visible()){
                this.xscroll_y = true;
            }else{
                this.xscroll_y = false;
            }
        },100),
        is_mouseover(){
            if(!this.inapp){
                this.mouseover = true;
                setTimeout(() => {
                    this.mouseover_show = this.mouseover;
                }, 300);
            }else{
                this.mouseover_show = false;
            }
        },
        is_mouseout(){
            if(!this.inapp){
                if(this.mouseover){
                    this.mouseover = false;
                    this.mouseover_show = this.mouseover;
                }
            }else{
                this.mouseover = false;
                this.mouseover_show = false;
            }
        },
        detail_video(data){
            var vm = this;
            if(this.isInApp){
                //vm.$router.push( { query : {pop_url : 'https://eatalk.co.kr/#/video/detail/nCBTlJ2099199127' } });
                vm.$router.push({ path: '/video/detail/' + data.video_id ,query : {keyword : this.$route.query.keyword} });
            }else{
                vm.$router.push({ path: '/video/detail/' + data.video_id ,query : {keyword : this.$route.query.keyword} });
            }
            //vm.$emit('set_play_id',data.video_id);
        },
        go_channel(data){
            var vm = this;
            vm.$router.push({ path: '/channel/detail/' + data.channel_no });
        },
        onImgLoad(){
            setTimeout(()=>{
                this.is_loading_end = true;
            },10);
        }
    },
    activated() {
        //this.is_play_show = false;
        this.mouseover_show = false;
    },
    created(){
        ts_no++;
        if(localStorage.getItem('DEV') == 'Y'){
            this.is_debug = true;
        }else{
            this.is_debug = false;
        }
    },
    mounted() {
        setTimeout(()=>{
            this.init_load_single = this.init_load;
            setTimeout(()=>{
                try{
                    this.width = $('#'+this.ts_ids+'thumb_'+this.vod_list.no).width();
                    this.is_offset = $('#'+this.ts_ids+'thumb_'+this.vod_list.no).offset().top;
                    this.is_left_offset = $('#'+this.ts_ids+'thumb_'+this.vod_list.no).offset().left;
                }catch(e){}
            },300);
            setTimeout(()=>{
                this.is_loading_end = true;
            },1000);
        },100);
    }
})
</script>

