<script>
var cpt_post_video = Vue.component('cpt_post_video', {
    template: '#cpt_post_video_tem',
    name: 'cpt_post_video',
    data(){
        return {
            xscroll:0,
            init_load_end:false,
            post_badge:[],
        }
    },
    computed:{
        rcount(){
            if(this.width < 520){
                return 4;
            }else if(this.width >= 520 && this.width < 680){
                return 4;
            }else if(this.width >= 680 && this.width < 980){
                return 4;
            }else if(this.width >= 980 && this.width < 1400){
                return 3;
            }else if(this.width >= 1400 && this.width < 1800){
                return 4;
            }else if(this.width >= 1800){
                return 5;
            }
        },
    },
    props:['post_list','init_load','title','row_count','width'],
    methods:{
        detail_post_video(num){
            var vm = this;

            vm.$router.push({ path: '/post/detail?no=' + num });
        },
        getPostbadge(){
            var vm = this;
            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/category/crud/list',
                data:{
                    sch_type:'post'
                },
                dataType: 'json',
                success: function(res, status) {
                    if(res.code==200) {
                        vm.post_badge = res.result.data;
                        vm.set_post_badge();
                    }else{
                        alert(res.message);
                    }
                }
            });
        },
        set_post_badge(){
            if(this.post_list){
                for(var i=0; i<this.post_list.length; i++){
                    for(var j=0; j<this.post_badge.length; j++){
                        if(this.post_badge[j].no == this.post_list[i].category_no){
                            this.post_list[i].color = this.post_badge[j].color;
                        }
                    }
                }
            }
        },
    },

    activated() {
        if(this.xscroll>0){
            $('#main_post_area').scrollLeft(this.xscroll);
        }
    },
    mounted() {
        var vm = this;
        this.getPostbadge();
        setTimeout(()=>{
            $('#main_post_area').scroll(function(){
                vm.xscroll = this.scrollLeft;
            });
        },1000);

        _.delay(()=>{
            this.init_load_end = true;
        },100)
    }
})
</script>

