<script>
var corePlayer;
var player;
var contbarshowtimer;
var contbarshowtimer_ps2;

var cpt_player_instance = null;

var cpt_player = Vue.component('cpt_player', {
        template: '#cpt_player_tem',
        name: 'cpt_player',
        props:['video_id','vod_data','run_autoplay','is_my'],
        data(){
            return {
                vod:[],
                thumb_sd:[],
                thumb_hd:[],
                play_url:[],
                width:0,
                height:0,
                isPlay:false,
                isEnd:false,
                playTime:0,
                isMuted:false,
                duration:0,
                loop:true,
                is_autoplay:'',
                isWrapShow:false,
                isWrapShowTimer:null,
                dtime:0,
                ptime:0,
                dtime_run_cnt:0,
                visible:true,
                playlist:[],
                api_url:'',
                dtimer: null,
                ptimer: null,
                init_timer: null,
                v_controlbar:false,
                v_controlbar_lock:false,

            }
        },
        computed:{
            back_link(){
                if(sessionStorage.getItem('last_router_path')){
                    return sessionStorage.getItem('last_router_path');
                }else{
                    return '';
                }
            },
            autoplay(){
                if(this.is_autoplay){
                    return true;
                }else{
                    if(this.run_autoplay){
                        return true;
                    }else{
                        return false;
                    }
                }
            },

            thumb_image_url(){
                if(this.vod.thumb_url){
                    return "" + this.vod.thumb_url;
                }else{
                    return "" + this.thumb_hd;
                }
            }


        },
        watch:{
            '$route'(to,from){
                console.log('from path' , from.path);
            },
            isPlay(to){
                if(to){
                    this.isEnd = false;
                }
            },
            dtime(to){
                //console.log('페이지 로드 후 ',to);
                if(this.dtime % 30 == 0){
                    this.update_page_time();
                }
            },
            ptime(to){


                if(this.ptime % 5 == 0){

                    console.log('재생 시간',to);

                    this.update_play_time();
                }
            },
            playTime(to){


                //console.log('현재 재생 위치',to);
            },
            vod(to){
                if(to.video_id){
                    this.initRun();
                }
            },
            v_controlbar(to){
                //console.log('is control bar',to);
            }

        },
        methods:{
            goBack(){
                if(history.length == 1){
                    this.$router.push('/');
                }else{
                    this.$router.go(-1);
                }
            },
            /*****************************************************************
            *
            *  SGRSOFT Developer : now17
            *  Date : 2021/10/19
            *  Description : 영상 재생 시간
            *
            *****************************************************************/
            update_play_time(){

                if(!this.visible) return;
                if(!user_key) return;

                $.ajax({
                    type: 'POST',
                    url: 'https://eatalk.live24.app/api/vod/json/play_time_update',
                    data:{
                        no:cpt_player_instance.vod.no,
                        sec:cpt_player_instance.ptime,
                        key:user_key,
                        play_time :cpt_player_instance.playTime
                    },
                    dataType: 'json',
                    success(res, status) {

                    },
                    error: function(request,status,error) {
                        console.log(request.responseText);
                    }
                });
            },
            /*****************************************************************
            *
            *  SGRSOFT Developer : now17
            *  Date : 2021/10/19
            *  Description : 페이지에 머문시간 체크
            *
            *****************************************************************/
            update_page_time(){

                if(this.dtime_run_cnt > 5) return;
                if(!this.visible) return;

                this.dtime_run_cnt ++;

                $.ajax({
                    type: 'POST',
                    url: 'https://eatalk.live24.app/api/vod/json/page_time_update',
                    data:{
                        no:cpt_player_instance.vod.no,
                        sec:cpt_player_instance.dtime,

                    },
                    dataType: 'json',
                    success(res, status) {

                    },
                    error: function(request,status,error) {
                        console.log(request.responseText);
                    }
                });


            },
            playerEvent(){
                player.on('canplay',res=>{
                    try { this.duration = round(player._corePlayer.duration); } catch(err){}
                });
                player.on('playing',res=>{
                    //console.log('재생시작');
                    this.isPlay = true;
                });
                player.on('play',res=>{
                    //console.log('재생시작');

                    this.isPlay = true;
                });
                player.on('pause',res=>{
                    //console.log('일시중지');
                    this.isPlay = false;
                });
                player.on('stop',res=>{
                    // console.log('중단');
                    this.isEnd = true;
                });
                player.on('timeupdate',res=>{
                    try {this.playTime = round(player._corePlayer.currentTime); } catch(err){}
                });
                player.on('error',res=>{
                    console.log('에러',res);
                });
                player.on('volumechange',res=>{
                    this.isMuted = player._corePlayer.muted;
                })

                //브라우저 비활성/활성시 비디오재생 제어
                document.addEventListener('visibilitychange', event=> {
                    if (document.hidden) {
                        this.visible = false;
                        // console.log('not visible');
                    } else {
                        this.visible = true;
                    }
                });

                player.on('ended',res=>{
                    //console.log('nextTrack','1212112');
                });

                player.on('abort',res=>{
                    var nextTrackInfo = player._playlist[player._currentIndex];

                    if(nextTrackInfo.vod_no){
                        player.pause();

                        //라우터 이동없이 갱신
                        //this.$emit('go_vod',nextTrackInfo.vod_no);
                        //트랙 이동
                        //player.prevTrack();
                        //player.nextTrack();
                        //player.firstTrack();

                        //라우터 갱신
                        this.$router.replace({path:'/video/detail/' + nextTrackInfo.vod_no , query : {p:this.vod.no}});
                    }
                    //console.log('nextTrack',player._playlist[player._currentIndex]);
                });





            },
            autoPlay(){
                //미니플레이어는 무조건 음소거
                player.mute(true);
                corePlayer.play().then(res=>{
                    console.log('자동재생 성공');
                }).catch(err=>{
                    console.log('자동재생 실패 - 음소거 시도')
                    player.mute(true);
                    setTimeout(()=>{
                        player.play();
                    },1)
                })
            },
            initRun(){

                this.autoheight();

                setTimeout(()=>{

                },100)

                this.init_player();

                //사용자 페이지 시간
                clearInterval(this.dtimer);
                this.dtimer = setInterval(()=>{
                    this.dtime++;
                },1000);

                //사용자 재생시간
                clearInterval(this.ptimer);
                this.ptimer = setInterval(()=>{
                    if(this.isPlay){
                        this.ptime++;
                    }
                },1000);
            },

            playRun(){
                player.play();
            },
            playToggle(){
                if(this.isPlay){
                    player.pause();
                }else{
                    player.play();
                }
            },
            autoheight(){
                //$('.autoheight, video').height($(window).height());
                //this.width = $(window).width();

            },
            unMute(){
                player.mute(false);
            },
            /*****************************************************************
            *
            *  SGRSOFT Developer : now17
            *  Date : 2021/10/13
            *  Description : 플레이어 초기화
            *
            *****************************************************************/
            init_player:_.debounce(function(){

                //요거 연구중..
               /* if(cpt_player_instance.vod.prev_vod){
                    this.playlist.push(
                        {
                            poster:cpt_player_instance.vod.prev_vod.thumb_url,
                            title:cpt_player_instance.vod.prev_vod.title,
                            file: cpt_player_instance.vod.prev_vod.play_url['hls']['abr'],
                            vod_no:cpt_player_instance.vod.prev_vod.no,
                        }
                    );
                }*/

                this.playlist.push(
                    {
                        //poster:cpt_player_instance.thumb_image_url,
                        title:cpt_player_instance.vod.title,
                        file: cpt_player_instance.play_url['hls']['abr'],
                        vod_no:cpt_player_instance.vod.video_id,
                    }
                )


                if(cpt_player_instance.vod.next_vod){
                    this.playlist.push(
                        {
                            poster:cpt_player_instance.vod.next_vod.thumb_url,
                            title:cpt_player_instance.vod.next_vod.title,
                            file: cpt_player_instance.vod.next_vod.play_url['hls']['abr'],
                            vod_no:cpt_player_instance.vod.next_vod.video_id,
                        }
                    );
                }



                player = new ncplayer('video1', {

                    autostart: cpt_player_instance.autoplay,
                    aspectRatio: '16:9',
                    playlist:cpt_player_instance.playlist

                });

                corePlayer = player._corePlayer.player;

                clearInterval(cpt_player_instance.init_timer);
                cpt_player_instance.init_timer = null;

                if(this.vod.play_info){
                    if(this.vod.play_info.sec>5){
                        setTimeout(()=>{
                            corePlayer.currentTime = this.vod.play_info.sec;
                        },1);
                    }
                }

                setTimeout(()=>{
                    this.playerEvent();
                },1)

                this.startingTimer = setTimeout(()=>{

                    if(this.autostart){
                        this.autoPlay();
                    }

                    //fixed now17 : 네이버 플레이어 버그 수정 , 전체 재생시간 못가져옴
                    try { player._corePlayer._duration  = parseInt(this.vod.duration); } catch(e){}

                },500)



            },200),
            /*****************************************************************
            *
            *  SGRSOFT Developer : now17
            *  Date : 2021/10/13
            *  Description : video api
            *
            *****************************************************************/
            async get_video_info(video_id){

                await  $.ajax({
                        type: 'GET',
                        url: this.api_url,
                        data:{
                            video_id:video_id,
                        },
                        dataType: 'json',
                        success: function(res, status) {
                            if(res.code==200){

                                cpt_player_instance.vod = res.result.data;
                                cpt_player_instance.thumb_sd = res.result.data.thumb_sd;
                                cpt_player_instance.thumb_hd = res.result.data.thumb_hd;
                                cpt_player_instance.play_url = res.result.data.play_url;

                                cpt_player_instance.get_vod_list();

                            }else{
                                alert(res.message);
                            }
                        },
                        error: function(request,status,error) {
                            console.log(request.responseText);
                        }
                });
            },

            /*****************************************************************
            *
            *  SGRSOFT Developer : now17
            *  Date : 2021/10/19
            *  Description : 플레이리스트
            *
            *****************************************************************/
            //영상 리스트 불러오기
            get_vod_list(){

                $.ajax({
                    type: 'GET',
                    url: 'https://eatalk.live24.app/api/vod/json/list',
                    data:{
                        row_count: 30,
                        page_no: 1,
                        key:sessionStorage.getItem('user_key'),
                        ref_no:cpt_player_instance.vod.no,
                    },
                    dataType: 'json',
                    success(res, status) {
                        if(res.result.data){
                            cpt_player_instance.vod.next_vod = res.result.data[0];
                            cpt_player_instance.$emit('set_vod_list', res.result.data);
                        }

                    }
                });
            },

            show_cplbar(){
                var vm = this;
                if(vm.v_controlbar_lock){
                    return
                }
                if($('.ncplayer-control-container').hasClass('show')){

                    vm.v_controlbar_lock = true;
                    vm.v_controlbar = true;

                }else{
                    vm.v_controlbar_lock = false;
                    setTimeout(()=>{
                        vm.v_controlbar = false;
                    },100)


                }

                setTimeout(()=>{
                    vm.v_controlbar_lock = false;
                },200)
            },

        },
        created(){

            cpt_player_instance = this;

            try {

                this.vod = JSON.parse('null');
                this.thumb_sd = JSON.parse('null');
                this.thumb_hd = JSON.parse('null');
                this.play_url = JSON.parse('null');

            } catch (err) {
                console.log('invalid vod information');
                debugger;
            } finally {

                this.initRun();
            }

            // console.log('[DEBUG] player created.');

        },
        mounted() {
            this.api_url = this.is_my ? 'https://eatalk.live24.app/api/vod/json/my_detail' : 'https://eatalk.live24.app/api/vod/json/detail';

            if(this.vod_data.video_id){
                if(!this.vod){
                    this.vod = this.vod_data;
                    this.initRun();
                    this.get_video_info(this.vod_data.video_id).then(res => {

                        clearInterval(contbarshowtimer);
                        contbarshowtimer = setInterval(()=>{
                            this.show_cplbar();
                        },1);

                    })
                }
            }

            document.addEventListener('fullscreenchange', (event) => {
                if (document.fullscreenElement) {
                    try{
                        window.eatalkapp.setScreenOrientation('landscape');
                    }catch(e){

                    }
                } else {
                    try{
                        window.eatalkapp.setScreenOrientation('portrait');
                    }catch(e){
                    }
                }
            });

        },

        beforeDestroy() {

            // 재생중이면 정지
            if(this.isPlay){
                player.pause();
            }

            this.isPlay = null;
            this.isEnd = null;
            this.is_autoplay = null;

            this.vod = null;
            this.vod_data = null;
            this.thumb_sd = null;
            this.thumb_hd = null;
            this.play_url = null;
        },

        destroyed() {

            clearTimeout(this.startingTimer); this.startingTimer = null;

            clearInterval(this.dtimer); this.dtimer = null;
            clearInterval(this.ptimer); this.ptimer = null;
            clearInterval(this.init_timer); this.init_timer = null;

            corePlayer = null;
            player = null;
            cpt_player_instance = null;

            clearInterval(contbarshowtimer);

            // console.log('[DEBUG] player destroyed.');
        }
})
</script>

