Vue.component('cpt_product_select', {
    template:'#cpt_product_select_tem',
    props: ['id',"product_no",'type','mode'],
    data(){
        return {
            products:[],
            keyword:'',
            is_product:[],
            last_value:'',
            product_no_value:'',
        }
    },
    watch:{

        keyword(to){
            this.search();
        },
        product_no_value(to){
            try{
                this.$emit('sync_value',to);
            }catch(e){

            }
        },
    },
    computed:{
        is_product_select(){
            if(this.is_product.product_no){
                return true;
            }
        },
        select_product_data(){
            return this.products.filter(res=>{
                return res.no == this.product_no_value
            });
        }
    },
    methods:{
        open_cafe_product_view(){

            window.open(store.state.cafe24_mall+'/disp/admin/product/ProductRegister?product_no='+this.is_product.product_no,'pr',"width=980, height=800")

        },
        select_product(data){
            this.is_product = data;
            this.product_no_value = data.no;
            this.products = [];
            this.keyword = '';
            this.last_value = '';


        },
        search:_.debounce(function(){
            this.run_search();
        },200),
        run_search(){
            var vm = this;
            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/product/json/search',
                data:{
                    like_product_name:vm.keyword,
                    no:vm.product_no_value
                },
                dataType: 'json',
                success: function(res, status) {

                    if(res.result.data){
                        vm.products = res.result.data;
                        if(!vm.keyword && vm.product_no_value){
                            if(res.result.data[0]){
                                vm.is_product = res.result.data[0];
                            }
                        }
                    }
                },
                error: function(request,status,error) {
                    console.log(request.responseText);
                }
            });
        }

    },
    created(){
        if(this.product_no >0){
            this.product_no_value = this.product_no;

            this.run_search();
        }else{
            this.product_no ='';
        }

    },

});
</script>

