</template>

<script>
var cpt_pull_to_refresh = Vue.component('cpt_pull_to_refresh', {
    template: '#cpt_pull_to_refresh_tem',
    name: 'cpt_pull_to_refresh',
    data(){
        return {
            show_refresh:false,
            base_height:70, //헤더 픽스된 사이즈
            refresh_start:'' ,
            refresh_move:'',
        }
    },
    props:[],
    methods:{
        on_touch_start(e){
            if(window.scrollY === 0){
                this.refresh_start = parseInt(e.touches[0].pageY);
                // console.log('터치시작')
            }
        },
        on_touch_end(e){
            const vm = this;
            if(this.refresh_move > 190){
                setTimeout(()=>{
                    vm.show_refresh = false;
                    vm.refresh_move = 0;
                    window.location.reload();
                },1500)
            }else{
                this.show_refresh = false;
                this.refresh_move = 0;
            }
            // console.log('터치 끝')
        },
        on_touch_move(e){

            if(window.scrollY === 0){
                if(this.refresh_move < 200){
                    this.refresh_move = this.base_height + parseInt( e.changedTouches[0].pageY) - this.refresh_start;
                }
                this.show_refresh = true;
            }else{
                this.show_refresh=false;
            }
        },

    },
    mounted(){
        //앱일경우에만 당겨서 새로고침
        // try{
        //     if (window.eatalkapp) {
        if(window.userAgent.indexOf('EatalkApp') > -1){
            setTimeout(()=>{
                window.addEventListener('touchend', this.on_touch_end);
                window.addEventListener('touchstart', this.on_touch_start);
                window.addEventListener('touchmove', this.on_touch_move);
            },100)
        }

    },
    destroyed() {
        window.removeEventListener('touchend', this.on_touch_end);
        window.removeEventListener('touchstart', this.on_touch_start);
        window.removeEventListener('touchmove', this.on_touch_move);
    },
})
</script>
<style>
.refresh_motion {
    text-align: center;
