<template id="cpt_infinity_scroll_tem">
    <div id="scroll" class="">
    </div>
</template>

<script>
var cpt_infinity_scroll = Vue.component('cpt_infinity_scroll', {
    template: '#cpt_infinity_scroll_tem',
    name: 'cpt_infinity_scroll',
    data(){
        return {
            init_load:false,
        }
    },
    props:['login_user_info','is_scroll'],
    watch:{
        is_scroll(val){
            if(val){
                this.scroll();
            }else{
                window.onscroll = '';
            }
        }
    },
    methods:{
      scroll(){
          var vm = this;
          //스크롤 바닥 감지

          window.onscroll = function(e) {
              // 스크롤 없는경우 추가
              if(window.innerHeight == document.body.scrollHeight){
                  return;
              }
              if((window.innerHeight + window.scrollY) >= document.body.scrollHeight-50) {
                  window.onscroll = '';   //한번에 스크롤이 되는것을 방지하여 이벤트를 한번 지워준다.
                  vm.$emit('last');
              }
          };
      }
    },
    destroyed() {
        window.onscroll = '';
    },
    mounted(){
        this.scroll();
    }
})
</script>

