<script>
var cpt_toast = Vue.component('cpt_toast', {
    template: '#cpt_toast_tem',
    name: 'cpt_toast',
    watch:{
        toast(val){
            if(val){
                this.show_toast(val);
            }
        }
    },
    data(){
        return{
            removeToast:'',
            text:'',
        }
    },
    computed: Vuex.mapGetters([
        'toast'
    ]),
    methods:{
        close(){
            document.getElementById("toast").classList.remove("reveal")
            this.$store.commit('toast','');
        },
        show_toast(string) {
            const toast = this.$refs.toast
            const vm = this;
            this.text = string;

            toast.classList.contains("reveal") ?
                (clearTimeout(this.removeToast), this.removeToast = setTimeout(function () {
                    document.getElementById("toast").classList.remove("reveal")
                }, 1000)) :
                this.removeToast = setTimeout(function () {
                    document.getElementById("toast").classList.remove("reveal")
                    vm.$store.commit('toast','');
                }, 2000)
            toast.classList.add("reveal");
            // toast.innerText = string;
            // toast.innerHTML = '<i @click="close()" class="ph-x p-l3"></i>'
        },
    },
    mounted(){
        if(this.$store.getters.toast){
            this.show_toast(this.$store.getters.toast);
        }
    },
    created() {

    }
})
</script>



