            is_comment_idx:0,
            is_comment_del_idx:0,

            ing_modfiy:false,
            target_no: 0,

            // key : 'd8a48bc40cf9275ce35137d53a78f842d7dc5be07ee1a4149fbbc782a5fd938a',
            comment_reply: false,
            sch_parent_no: 0,

        }
    },
    props:['num','login_user_info','post_detail','init_comment','type'],
    watch:{
        comment_cnt(newVal, oldVal){
            $('.replyTotCnt').text(parseInt(newVal));
            $('.cmt_cnt').text(parseInt(newVal));
        },
    },
    methods:{
        flag_reply(){
            this.init();
        },
        player_pause(){
            if(this.type == 'vod'){
                player.pause();
            }
        },
        player_play(){
            if(this.type == 'vod') {
                player.play();
            }
        },
        show_reply(comment) {
          var vm = this;

          vm.comments_reply = [];
          vm.comment_reply = true;
          vm.sch_parent_no = comment.no;
          vm.is_reply_data.push(comment);
          vm.get_comment_reply();
        },
        reset_comment(){
            var vm = this;
            vm.comments = [];
            vm.comment_cnt = [];
            vm.get_comment();
        },
        get_comment() {
            var vm = this;

            $.ajax({
                type: "GET",
                url: 'https://eatalk.live24.app/api/comment/json/list',
                data: {
                    sch_type: vm.type,
                    sch_target_no: vm.target_no,
                    page_no: vm.page_no,
                    row_count : 1000,
                },
                success: function (res, status) {
                    for(var i=0; i<res.result.data.length; i++){
                        res.result.data[i].is_menu_view = false;
                    }
                    vm.comments = [...res.result.data];
                    vm.comment_cnt = res.result.data.length;
                    if(!vm.comment_reply) {
                        vm.sch_parent_no = ''
                    }
                },
                error: function (request, status, error) {
                    console.log(request.responseText);
                },
            });
        },
        get_comment_reply() {
            var vm = this;

            $.ajax({
                type: "GET",
                url: 'https://eatalk.live24.app/api/comment/json/list',
                data: {
                    sch_type: vm.type,
                    sch_target_no: vm.target_no,
                    sch_parent_no: vm.sch_parent_no,
                    page_no: vm.page_no,
                    row_count : 1000,
	                order_type: 'asc'
                },
                success: function (res, status) {
                    for(var i=0; i<res.result.data.length; i++){
                        res.result.data[i].is_menu_view = false;
                    }
                    vm.comments_reply = [...res.result.data];
                    vm.$emit("comment_reply", vm.comment_reply);
                },
                error: function (request, status, error) {
                    console.log(request.responseText);
                },
            });
        },
        smenu_show(comment) {
            var is_menu_yn = comment.is_menu_view;
            this.smenu_all_close();
            if(is_menu_yn){
                comment.is_menu_view = false;
            }else{
                comment.is_menu_view = true;
            }
        },
        smenu_all_close(){
            for(var i=0; i<this.comments.length; i++){
                this.comments[i].is_menu_view = false;
            }
        },
        login_chk(){
            var vm = this;
            if(!this.login_user_info.no) {
                sgr_custom.confirm('로그인 후 이용하실 수 있습니다.\n<br>로그인 하시겠습니까?',{
                    ycb(){
                        vm.$emit('run_login');
                    },
                    ybt:'예',
                    nbt:'아니오'
                });
                /**/
                parent.postMessage({'action': 'loginConfirm'}, "*");
                return false;
                /**/
            }
        },
        add_comment : _.debounce(function (){
            var url = ''
            this.login_chk();
            if(this.send_lock){
                parent.postMessage({action: 'alertMsg',message:'댓글 등록중입니다. 잠시 후 시도해주세요.'}, "*");
                return false;
            }
            this.send_lock = true;

            var vm = this;
            if(vm.ing_modfiy) {
                url = 'https://eatalk.live24.app/api/comment/json/edit'
            }else {
                url = 'https://eatalk.live24.app/api/comment/json/write'
            }
            if(vm.comment_reply){
                var allData = { "type" : vm.type, "target_no" : vm.target_no, "key" : vm.user_key, "comment_no" : vm.is_comment_data.no, "db_parent_no" : vm.sch_parent_no, "db_comment_txt" : vm.db_comment_txt };
            }else{
                var allData = { "type" : vm.type, "target_no" : vm.target_no, "key" : vm.user_key, "comment_no" : vm.is_comment_data.no, "db_comment_txt" : vm.db_comment_txt };
            }

            if (!url) debugger;

            $.ajax({
                type: "POST",
                url: url,
                data: allData,
                dataType: 'json',
                success: function (res, status) {
                    if (res.code == 200) {
                        vm.is_comment_data.is_modify = false;
                        vm.is_comment_data.comment_txt = res.result.data.comment_txt;
                        vm.db_comment_txt = '';
                        vm.get_comment();
                        if(vm.comment_reply){
                            vm.get_comment_reply();
                        }
                        vm.ing_modfiy = false;

                        $('#cmt_body').scrollTop(0);

                        setTimeout(() => {
                            vm.send_lock = false;
                        }, 1000);
                        // sgr_common.toast('댓글이 등록 되었습니다.');
                    } else {

                        alert(res.message);

                        setTimeout(() => {
                            vm.send_lock = false;
                        }, 1000);
                    }
                },
                error: function (request, status, error) {
                    console.log(request.responseText);
                },
            });
        },300),
        del_comment(data,idx){
            var vm = this;
            this.is_comment_data = [];
            this.is_comment_data = data;
            this.is_comment_del_idx = idx;
            this.smenu_all_close();

            sgr_custom.confirm('댓글을 삭제할까요?',{
                ycb:function(){
                    vm.del_comment_run();
                },
                ybt:'예',
                nbt:'아니오'
            })
        },
        del_comment_run(){
            if(this.is_comment_data) {
                var vm = this;
                $.ajax({
                    type: 'POST',
                    url: 'https://eatalk.live24.app/api/comment/json/del',
                    data: {
                        comment_no: vm.is_comment_data.no,
                        key:vm.user_key,
                    },
                    dataType: 'json',
                    success: function (res, status) {
                        if (res.code == 200) {
                            vm.get_comment();
                            vm.cancel_modify_run();
                            if(!vm.comment_reply) {
                                vm.sch_parent_no = ''
                            }
                            sgr_common.toast('댓글이 삭제 되었습니다.');
                        }
                        parent.postMessage({action: 'toastMsg',message:res.message}, "*");
                        vm.is_comment_data = [];
                        vm.is_comment_del_idx = '';
                        vm.is_reply_data = [];
                        vm.comment_reply = false;
                    },
                    error: function (request, status, error) {
                        console.log(request.responseText);
                    }
                });
            }

        },
        modify_comment(data,idx){
            this.is_comment_data = ''
            this.is_comment_data = data;
            this.is_comment_idx = idx;
            this.ing_modfiy = true;
            this.is_comment_data.is_modify = true;
            this.is_comment_data.is_menu_view = false;

            this.db_comment_txt = this.is_comment_data.comment_txt.replace(/@([^\|\s\t]+)([^\s\t]+)?/g, '@$1');

            $('#db_comment_txt').focus();
            this.smenu_all_close();
        },
        cancel_modify(){
            var vm = this;
            sgr_custom.confirm('댓글 수정을 취소할까요?',{
                ycb:function(){
                    vm.cancel_modify_run();
                },
                ybt:'예',
                nbt:'아니오'
            })

        },
        cancel_modify_run(){
            this.is_comment_data.is_modify = false;
            this.ing_modfiy = false;
            this.db_comment_txt = '';
            this.$set(this.comments, this.is_comment_idx,this.is_comment_data);
            this.is_comment_data = [];
        },
        init(){
            this.channel_no = this.login_user_info.channel_no;
            this.comment_reply = false;
            this.is_reply_data = [];
            this.ing_modfiy = false;
            this.db_comment_txt = ''
            this.comment_cnt = 0;
            this.get_comment();
            this.get_channel();
        },
        get_channel() {
            var vm = this;

            if(!this.isLogin){
                return;
            }

            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/channel/json/detail',
                data: {
                    no: vm.channel_no,
                },
                dataType: 'json',
                success: function (res, status) {
                    if (res.code == 200) {
                        vm.channel_data = res.result.data;
                        vm.profile_image_url = res.result.data.channel_profile_img;
                    } else {
                        alert(res.message);
                    }
                }
            });
        },
        report_pop(data ,idx){
            var vm = this;
            vm.smenu_all_close();

            if(!this.isLogin){
                vm.login_chk();
                return;
            }

            var locale = {
                OK: 'I Suppose',
                CONFIRM: '신고하기',
                CANCEL: '취소',
            };

            bootbox.addLocale('custom', locale);

            bootbox.prompt({
                placeholder: '신고 사유를 입력해주세요',
                title: '<i class="ph-warning-octagon "></i> 댓글 신고',
                message:'<div class="p-1  ft12">댓글이 불쾌하거나 문제가 있다면 신고해주세요.<br>운영진에서 바로 조치해드립니다.</div><div class="p-1 m-b10 ft12 text-danger">(신고하신 댓글은 사용자에게 바로 차단됩니다.)</div>',
                centerVertical:true,
                inputType: 'textarea',
                locale: 'custom',
                callback: function (result) {
                    if(result){
                        vm.add_report(data, idx);
                    }
                }
            });
        },
        add_report(data, idx){
            var vm = this;

            $.ajax({
                type: 'POST',
                url: 'https://eatalk.live24.app/api/report/json/write',
                data:{
                    key : vm.user_key,
                    type : 'comment',
                    target_no : data.no,
                    db_report_txt : data.comment_txt
                },
                dataType: 'json',
                success: function(res, status) {
                    if(res.code==200){
                        var msg = "[" + res.result.data.report_txt + "] 가 신고 완료 되었습니다.";
                        sgr_common.toast(msg);
                        vm.reset_comment();
                    }else{
                        alert(res.message);
                    }
                },
                error: function(request,status,error) {
                    console.log(request.responseText);
                }
            });
        },
        get_click_event(e) {
            if (e.target.className != 'drop_menu_btn') {
                this.smenu_all_close();
            }
        },
        go_channel(data){
            this.$router.push('/channel/detail/'+data.user_no_info.channel_no);
        }
    },
    mounted() {
        var vm = this;
        document.body.addEventListener('click', vm.get_click_event);
    },
    beforeDestroy() {
        var vm = this;
        document.body.removeEventListener('click', vm.get_click_event);
    },
    computed:{
        comment_placeholder(){
            if(this.login_user_info.no > 0){
                if(this.send_lock){
                    return '내용을 등록중입니다.';
                }else {
                    return '내용을 입력해주세요.';
                }
            }else{
                return '로그인 후 댓글을 입력해주세요.';
            }
        },
        isLogin(){
            return this.login_user_info.no > 0;
        },
        user_key(){
            return this.login_user_info.user_key;
        }
    },
    created() {
        this.channel_no = this.login_user_info.channel_no;
        this.comments = this.init_comment;
        this.target_no = this.num;
        this.comment_reply = this.init_reply;
        this.get_comment();
        this.get_channel();
    },
})
</script>

