<script src=" https://cdnjs.cloudflare.com/ajax/libs/summernote/0.8.18/lang/summernote-ko-KR.min.js"></script>
<template id="cpt_webeditor_tem">
	<div class="cpt_webeditor_tem pos_r">
		<textarea :id="'db_'+id" class="form-control summernote db_forms" style="width:100%;">{{content}}</textarea>
	</div>
</template>

<script>
var webeditor_timer;
Vue.component('cpt_webeditor', {
	template:'#cpt_webeditor_tem',
	props: ['id','content','height'],
	computed:{
		max_height(){
			return this.height * 4;
		}
	},
    data(){
        return {
            etimer:null,
            etimer_fail_cnt:0,
        }
    },
	methods:{
		ncp_image_uploader(file, editor){
			data = new FormData();
			data.append("file", file);
			data.append('field_name', 'file');
			data.append('file_dir', 'webeditor');
			$.ajax({
				data : data,
				type : "POST",
				url : "https://eatalk.live24.app/api/file/s3_img_upload",
				contentType : false,
				processData : false,
				success : function(data) {
					//항상 업로드된 파일의 url이 있어야 한다.
					console.log('data',data);
					$(editor).summernote('insertImage', data.result.filename);
				}
			});
		},
        set_editor(lang){
            var vm = this;
            $('.summernote').summernote({
                height: vm.height,
                maxHeight: vm.max_height,
                lang:"ko-KR",

                placeholder: "내용을 입력해주세요",
                toolbar: [
                    ['fontsize', ['fontsize']],
                    ['style', ['bold', 'italic', 'underline']],
                    ['font', ['strikethrough']],
                    ['color', ['color']],
                    ['para', ['paragraph']],
                    ['height', ['height']],
                    ['Insert', ['table']],
                    ['Insert', ['picture']],
                    ['Insert', ['video']],
                    ['Insert', ['link']],
                    ['code', [ 'codeview']],
                    ['view',['help']]
                ],
                callbacks: {
                    //이미지를 첨부
                    onInit: function() {
                        jsload('https://eatalk.live24.app/assets/lib/dropify/dist/js/dropify.min.js',true,true);
                        setTimeout(()=>{
                            $('.note-image-input').dropify()
                        },1000)
                    },
                    onImageUpload : function(files) {
                        //이미지 업로드
                        if(files[0].size > 2048013){
                            alert('이미지 용량이 2MB를 넘을 수 없습니다.');
                        }else{
                            vm.ncp_image_uploader(files[0],this);
                        }
                    },
                    onPaste: function (e) {
                        //이미지 붙여넣기
                        var clipboardData = e.originalEvent.clipboardData;

                        if (clipboardData && clipboardData.items && clipboardData.items.length) {
                            var item = clipboardData.items[0];
                            if (item.kind === 'file' && item.type.indexOf('image/') !== -1) {
                                e.preventDefault();
                            }
                        }
                    },
