<script>
var cpt_popup_event = Vue.component('cpt_popup_event', {
    template: '#cpt_popup_event_tem',
    name: 'cpt_popup_event',
    props:['ctime'],
    data() {
        return {
            popup: [],
            idx:0,
            is_view: false,
            timer:'',
            total:0,
            in_change:false,

        }
    },
    watch:{
        is_view(to){
            //홈에서만 나오게 체크
            if(this.$route.path == '/feed/home' ){
                if(to){
                    if(this.is_view){
                        if(!this.$route.query.popup_url){
                            $('body,html').css('overflow', 'hidden');
                        }
                    }
                }else{
                    $('body,html').css('overflow','unset');
                    clearInterval(this.timer)
                    this.$emit('popup_run_toggle');
                }
            }
        },

    },
    computed:{
        today(){
            let today = new Date();
            var year = today.getFullYear();
            var month = ('0' + (today.getMonth() + 1)).slice(-2);
            var day = ('0' + today.getDate()).slice(-2);

            var dateString = year + month  + day;

            return dateString;
        },
        is_popup(){
            return this.popup[this.idx];
        },
        target(){
            if(this.is_popup.link.indexOf('eatalk.co.kr') || this.is_popup.link.indexOf('ipmonster.cafe24.com')){
                return '_self'
            }else{
                return '_blank'
            }
        }
    },
    methods: {
        no_today() {
            localStorage.setItem('no_popup',this.today);
            this.is_view=false;
        },
        get_list() {
            var vm = this;


            var res = get_cache('main_popups');
            if(res){
                vm.popup = res.result.data;
                vm.total = res.result.total;

                if(vm.total>0){
                    vm.is_view = true;
                    vm.$emit('popup_run_toggle');
                }
                vm.set_timer();

                return ;
            }


            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/popup_event/json/list',
                data: {
                    row_count: 30,
                },
                dataType: 'json',
                success: function(res, status) {
                    if(res.code == 200){
                        vm.popup = res.result.data;
                        vm.total = res.result.total;

                        if(vm.total>0){
                            vm.is_view = true;
                            vm.$emit('popup_run_toggle');
                        }
                        vm.set_timer();

                        set_cache('main_popups',res,60);
                    }
                },
                error: function(request, status, error) {
                    console.log(request.responseText);
                }
            });
        },
        set_timer(){
            if(this.total>1){

                this.timer = setInterval(() => {
                    if(!this.in_change){
                        if(this.idx < (this.total - 1)){
                            this.idx++;
                        }else{
                            this.idx = 0;
                        }
                    }
                }, this.ctime);

            }
        },
        next(){
            this.in_change = true;
            if(this.idx >= (this.total-1)){
                this.idx=0;
            }else{
                this.idx++;
            }
            setTimeout(()=>{
                this.in_change = false;
            },this.ctime)

        },
        prev(){
            console.log('prev');
            if(this.idx == 0){
                this.idx = this.total-1;
            }else{
                this.idx--;
            }
            setTimeout(()=>{
                this.in_change = false;
            },this.ctime)

        },
        init_run(){
            if(localStorage.getItem('no_popup')){
                if(localStorage.getItem('no_popup') < this.today){
                    this.get_list();
                }
            }else{
                this.get_list();
            }
        }


    },
    created() {
        //fixed now17 : 팝업이 너무 빨리떠서.. 딜레이 추가
       setTimeout(() => {
           this.init_run()
       },1000);
    }
})
</script>

