                    if(!this.in_change){
                        if(this.idx < (this.total - 1)){
                            this.idx++;
                        }else{
                            this.idx = 0;
                        }
                    }
                }, this.ctime);

            }
        },
        next(){
            this.in_change = true;
            if(this.idx >= (this.total-1)){
                this.idx=0;
            }else{
                this.idx++;
            }
            setTimeout(()=>{
                this.in_change = false;
            },this.ctime)

        },
        prev(){
            if(this.idx == 0){
                this.idx = this.total-1;
            }else{
                this.idx--;
            }
            setTimeout(()=>{
                this.in_change = false;
            },this.ctime)

        }


    },
    destroyed() {
        setTimeout(() => {
            $('.main_videos')[0].pause();
        },100)
    },
    activated() {
        if(this.re_call){
            return
        }
        this.re_call = true;
        this.get_list();


    },
    created() {
        if(this.re_call){
            return
        }
        this.re_call = true;
        this.get_list();

    },
})
</script>

