                        vm.set_event();

                    }

                });

            }else{
                if(!localStorage.getItem('DEV')){
                    var res = get_cache('main_vod_box_' + vm.data.type + vm.data.no);
                    if(res && !sessionStorage.getItem('is_block_vod')){
                        vm.vod_list = res.result.data;
                        vm.init_load = true;
                        vm.set_event();

                        return;
                    }
                }else{
                    console.log('no cache')
                }

                $.ajax({
                    type: 'GET',
                    url: 'https://eatalk.live24.app/api/vod/json/list',
                    data: params,
                    dataType: 'json',
                    success(res, status) {
                        vm.vod_list = res.result.data;
                        vm.init_load = true;
                        vm.loading = false;

                        sessionStorage.removeItem('is_block_vod')
                        set_cache('main_vod_box_'+vm.data.type+vm.data.no,res,180);
                        vm.set_event();
                    }
                });
            }
        },
        set_event(){
            var vm = this;
            setTimeout(()=>{
                $('#box_slick_'+this.data.no).scroll(function(){
                    vm.xscroll = this.scrollLeft;
                });
            },1000);
        },
        click_scroll(arg, no) {
            var card_box = $('#box_slick_'+ no);
            var total_scroll = card_box.get(0).scrollWidth;
            var scroll_position = card_box.scrollLeft();
            var card_width = $('.video-grid').width();

            if (arg == 1) { // next
                if (scroll_position < total_scroll) {
                    card_box.animate({scrollLeft: scroll_position + card_width}, 500);
                } else {
                    card_box.animate({scrollLeft: total_scroll}, 500);
                }
            } else { // prev
                if (scroll_position > card_width) {
                    card_box.animate({scrollLeft: scroll_position - card_width}, 500);
                } else {
                    card_box.animate({scrollLeft: 0}, 500);
                }
            }
        },
    },
    created() {
        this.width = $(window).width();

        $(window).resize(()=>{
            this.width = $(window).width();
        });
    },
    mounted() {
        var vm = this;
        setTimeout(()=>{
            this.is_offset = $('#box_'+this.data.no).offset().top;

            if(this.is_run){
                this.load_vod_list();
            }
        },100);
    },
    activated() {
        if(this.xscroll > 0){
            $('#box_slick_'+this.data.no).scrollLeft(this.xscroll);
        }
    }
})


