            popup_view:false,
        }
    },
    computed:{
        this_scroll_offset(){
            return this.window_scroll + $(window).height() + 100 ;

        },
        is_run(){

            if(this.this_scroll_offset > this.is_offset && !this.init_load && this.mounted_end){
                return true;
            }
        },
        mobile_chk(){
            return /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
        },
        rcount(){
            if(this.width < 520){
                return 6;
            }else if(this.width >= 520 && this.width < 680){
                return 6;
            }else if(this.width >= 680 && this.width < 980){
                return 6;
            }else if(this.width >= 980 && this.width < 1400){
                return 6;
            }else if(this.width >= 1400 && this.width < 1800){
                return 8;
            }else if(this.width >= 1800){
                return 10;
            }
            //Api/Member/Oauth2ClientCallback/apple
        },
        row_count(){
            if(this.width < 520){
                return 5;
            }else if(this.width >= 520 && this.width < 680){
                return 5;
            }else if(this.width >= 680 && this.width < 980){
                return 5;
            }else if(this.width >= 980 && this.width < 1900){
                return 8;
            }else if(this.width >= 1900){
                return 10;
            }
        },
        isLogin(){
            return this.login_user_info.no > 0;
        },
        user_key(){
            return this.login_user_info.user_key;
        }

    },
    watch:{
        window_scroll(to){
            if(this.is_run){
                this.get_vod_list();
            }
        }
    },
    methods: {
        popup_run_toggle(){
            var vm = this;
            this.is_popup_run ^= true;
        },
        last_scroll(){
            //console.log('마지막 스크롤')
            var vm = this;
            this.is_scroll = false;

            _.delay(function(){
                vm.get_vod_list();
            })

        },
        //영상 리스트 불러오기
         get_vod_list(){
            var vm = this;

            if(vm.loding){
                return;
            }else{
                vm.page_no++;
                vm.loding = true;
                var res = get_cache('main_get_vod_list_' + vm.page_no);

                if(res){
                    vm.vod_list.push(...res.result.data);
                    if(vm.row_count > res.result.data.length){
                        vm.is_scroll = false;
                    }else{
                        vm.is_scroll = true;
                    }
                    vm.init_load = true;
                    vm.loding = false;
                    return false;
                }

                $.ajax({
                    type: 'GET',
                    url: 'https://eatalk.live24.app/api/vod/json/main_list',
                    data: {
                        row_count: vm.row_count,
                        page_no: vm.page_no,
                        key: vm.user_key,
                        timekey: timekey
                    },
                    dataType: 'json',
                    success(res, status) {
                        vm.vod_list.push(...res.result.data);
                        if(vm.row_count > res.result.data.length){
                            vm.is_scroll = false;
                        }else{
                            vm.is_scroll = true;
                        }
                        vm.init_load = true;
                        vm.loding = false;
                        set_cache('main_get_vod_list_' + vm.page_no, res, 30);
                    }
                });
            }

        },
        //post 전체 페이지로 이동
        go_post_list(title,type,value,order_col){
            if(type=='post'){
                this.$router.push({ path: '/post' });
            }else{
                const info = {title:title,type:type, value:value, order_col:order_col};
                localStorage.setItem('home_more_info',JSON.stringify(info));
                this.$router.push({ path: '/feed/home/more'});
            }
        },
        get_main_list(){
            var vm = this;
            var res = get_cache('main_box_jsons');

            if(res){
                vm.boxs = res.result.data;
                _.delay(()=>{
                    vm.popup_view = true;
                },1000);
                return ;
            }
            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/main_vod_boxs/json/list',
                dataType: 'json',
            	success: function(res, status) {
            		vm.boxs = res.result.data;

                    set_cache('main_box_jsons',res,60);

                    _.delay(()=>{
                        vm.popup_view = true;
                    },1000);
            	},
            	error: function(request,status,error) {
            		console.log(request.responseText);
            	}
            });
        }
    },
    activated() {
        this.is_scroll = true;
        this.scrollEnabled = true;

        if(this.window_scroll > 0){
            $(window).scrollTop(this.window_scroll);
        }

        this.$emit('lnb_last_pagh_set',this.$route.path);
    },
    deactivated() {
        this.is_scroll = false;
        this.scrollEnabled = false;
    },
    created(){
        this.width = $(window).width();
        window.addEventListener('resize',()=>{
            this.width = $(window).width();
        });
    },
    mounted(){
        var vm = this;

        this.get_main_list();

        setTimeout(()=>{
            this.is_offset = $('#main_vod_list_area').offset().top;
            this.mounted_end = true;
        },1000);
    }
})
</script>

