    components: ['cpt_video_col_card','cpt_titlebar','cpt_infinity_scroll'],
    props: ['login_user_info','window_scroll'],
    data(){
        return {
            init_load:false,
            post_list: [],
            title:'',
            type:'',
            value:'',
            order_col:'',
            row_count:15,
            vod_list:[],
            width:0,
            page_no:1,
            is_scroll:true,
            scrollEnabled:false,

        }
    },
    computed:{
        user_key(){
            return this.login_user_info.user_key;
        }
