            this.scrollEnabled = true;
        }
    },
    methods:{
        last_scroll(){
            this.is_scroll = false;
            this.page_no++;
            this.get_list();
        },
        get_list(){
            var vm = this;

            var params ={
                row_count: vm.row_count,
                page_no: vm.page_no,
                key:vm.user_key,
            }

            if(this.type=='category'){
                params ={
                    row_count: vm.row_count,
                    page_no: vm.page_no,
                    key:vm.user_key,
                    sch_category_no:this.value,
                    order_col:this.order_col,
                    shuffle:true,
                }
