            if(this.type=='search'){
                params ={
                    row_count: vm.row_count,
                    page_no: vm.page_no,
                    key:vm.user_key,
                    search:this.value,
                    order_col:this.order_col,
                    shuffle:true,
                }
            }

            if(this.type=='product'){
                params ={
                    row_count: vm.row_count,
                    page_no: vm.page_no,
                    key:vm.user_key,
                    sch_product_no:this.value,
                    order_col:this.order_col,
                    shuffle:true,
                }
            }

            if(this.type=='channel'){
                params ={
                    row_count: vm.row_count,
                    page_no: vm.page_no,
                    key:vm.user_key,
                    sch_channel_no:this.value,
                    order_col:this.order_col,
                    shuffle:true,
                }
            }

            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/vod/json/list',
                data: params,
                dataType: 'json',
                success(res, status) {
                    // vm.vod_list = res.result.data;
                    vm.vod_list.push(...res.result.data);
                    vm.init_load = true;
                    vm.loading = false;
                    vm.is_scroll = vm.row_count <= res.result_cnt;

                    // vm.set_event();
                }
            });

        },
        mobile_chk(){
            if(/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)){
                this.is_mobile = true;
                this.view_viedo = 3
            }
        },
    },
    destroyed() {
        this.is_scroll = false;
    },
    created() {
        this.is_scroll = true;
        this.scrollEnabled = true;
        this.width = $(window).width();
        window.addEventListener('reszie',()=>{
            this.width = $(window).width();
        })
        window.scrollTo(0,0);
        const info = JSON.parse(localStorage.getItem('home_more_info'))
        this.title = info.title
        this.type = info.type
        this.value = info.value
        // this.row_count = info.row_count
        this.order_col = info.order_col

    },
    mounted(){
        this.mobile_chk();
        setTimeout(()=>{
            this.get_list();
        },210);
    },
})
</script>

