            select_sort:'popularity', //(popularity : 좋아요순)(created:최신순)()
            scroll:0,
            page_no:1,
            row_count:30,
            is_scroll:true,
            vod_init_load:false,
            scrollEnabled:false,
            user_key:'',
        }
    },

    watch:{
        select_sort(){
            this.page_no = 1;
            this.vod_list = [];
            this.$store.commit('exp_sort',this.select_sort);
            this.get_vod();
        },
        $route(to,from){
            this.scrollEnabled = false;
            this.scrollEnabled = true;
        }
