            this.is_scroll = false;
            this.page_no++;
            this.get_vod();
        },
        goList(){
            this.$router.push('/channel/list/recommand')
        },
        getChannel(){
            var vm = this;

            //캐시있으면 활용
            var res = get_cache('exp_channel_list_'+vm.row_count);
            if(res && !sessionStorage.getItem('is_block_vod')){
                vm.channel_list = res.result.data;
                vm.init_load = true;
                return;
            }

            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/channel/json/list',
                data:{
                    row_count : vm.row_count,
                    sch_is_recom:'Y',
