            var res = get_cache('exp_vod_list_'+vm.page_no+'_'+vm.select_sort);
            if(res && ! sessionStorage.getItem('is_block_vod')){
                vm.init_load = true;
                vm.vod_init_load= true;
                vm.vod_list.push(...res.result.data);
                vm.is_scroll = vm.row_count <= res.result_cnt;
