                type: 'GET',
                url: 'https://eatalk.live24.app/api/vod/json/list',
                data:{
                    row_count : vm.row_count,
                    page_no: vm.page_no,
                    order_col : vm.select_sort,
                    key:user_key,
                },
                dataType: 'json',
                success: function(res, status) {
                    if(res.code==200) {
                        vm.init_load = true;
                        vm.vod_init_load= true;
                        vm.vod_list.push(...res.result.data);
                        vm.is_scroll = vm.row_count <= res.result_cnt;
                        sessionStorage.removeItem('is_block_vod')
                        set_cache('exp_vod_list_'+vm.page_no+'_'+vm.select_sort,res,30);
                    }else{
	                    bootbox.alert(res.message);
                    }
                }
            });
        }),
    },

    activated() {
        this.is_scroll = true;
        this.scrollEnabled = true;

        this.user_key = sessionStorage.getItem('user_key');
        if(localStorage.getItem('user_key')){
            this.user_key = localStorage.getItem('user_key');
        }
        this.$emit('lnb_last_pagh_set',this.$route.path);
    },
    deactivated() {
        this.is_scroll = false;
        this.scrollEnabled = false;
    },

    created(){
        window.scrollTo(0,0);

        this.user_key = sessionStorage.getItem('user_key');
        if(localStorage.getItem('user_key')){
            this.user_key = localStorage.getItem('user_key');
        }

        this.getChannel();
        this.get_vod();
        //this.select_sort = this.$store.getters.exp_sort;
        this.is_scroll = false;
        this.scrollEnabled = true;
    },
})
</script>

