<script>
var pe_subscribe = Vue.component('pe_subscribe', {
	template: '#pe_subscribe_tem',
    components:['cpt_video_card','cpt_titlebar','cpt_horizontal_scroll','cpt_pull_to_refresh'],
    props:['login_user_info'],
    computed:{
        isLogin(){
            return this.login_user_info.no > 0;
        },
        user_key(){
            return this.login_user_info.user_key;
        }
    },
	data(){
		return {
            init_load :false,
            select_sort: 'created',
            scroll:0,
            channel_list:[],
            row_count:30,
            page_no:1,
            vod_list : [],
            is_scroll:true,
            vod_init_load : false,
            scrollEnabled:false,
		}
	},
    watch:{
        select_sort(){
            this.page_no = 1;
            this.vod_list = [];
            this.$store.commit('sub_sort',this.select_sort)
            this.get_vod();
        },
        isLogin(to){
            if(to){
                this.init_run();
            }
        },
        $route(to,from){
            this.scrollEnabled = false;
            this.scrollEnabled = true;
        }
    },
    methods: {
        go_page(page){
            this.$router.push(page)
        },
        last_scroll(){
            this.is_scroll = false;
            this.page_no++;
            this.get_vod();
        },
	    go_list(){
	        this.$router.push('/channel/list/sub')
        },
        get_channel(){
	        var vm = this;


	        return new Promise(function(resolve, reject){

                $.ajax({
                    type: 'GET',
                    url: 'https://eatalk.live24.app/api/subscribe/json/list',
                    data:{
                         key : vm.user_key,
                    },
                    dataType: 'json',
                    success: function(res, status) {
                        if(res.code==200) {
                            vm.init_load = true;
                            vm.channel_list = res.result.data;
                            resolve(res.result.total)
                        }else{
                            //alert(res.message);
                            reject(res.message);
                        }
                    }
                });
            });
        },
        get_vod(){
            var vm = this;
            return new Promise(function(resolve, reject){
                $.ajax({
                    type: 'GET',
                    url: 'https://eatalk.live24.app/api/vod/json/my_sub_list',
                    data:{
                        key : vm.user_key,
                        row_count : vm.row_count,
                        page_no : vm.page_no,
                        order_col : vm.select_sort
                    },
                    dataType: 'json',
                    success: function(res, status) {
                        if(res.code==200) {
                            vm.init_load = true;
                            vm.vod_init_load= true;
                            vm.vod_list.push(...res.result.data);
                            if(vm.row_count > res.result_cnt){
                                vm.is_scroll = false;
                            }else{
                                vm.is_scroll = true;
                            }
                            resolve(res.result.total)
                        }else{
                            //alert(res.message);
                            reject(res.message);
                        }
                    }
                });
            });
        },
        init_run(){

            this.get_channel().then((cnt)=>{
                if(cnt>0){
                    this.get_vod();
                    this.scrollEnabled = true;
                }
            });
            this.select_sort = this.$store.getters.sub_sort;
        }
    },

    activated() {
	    this.is_scroll = true;
        this.scrollEnabled = true;
        this.$emit('lnb_last_pagh_set',this.$route.path);
    },
    deactivated() {
        this.is_scroll = false;
        this.scrollEnabled = false;
    },

    created(){
        if(this.isLogin){
            this.init_run();

        }
    },
})
</script>

