<script>
var pe_mypage = Vue.component('pe_mypage', {
    template: '#pe_mypage_tem',
    components: ['cpt_post_video','cpt_titlebar'],

    data () {
        return {
            init_load:true,
            review_cnt:0,

            my_data:'',
            initTimer:'',
            member_1:'',
            group_name:'',
            group_no : '',
            showMembership : false,
            all_member:[],
            devmode_click:0,

        }
    },
    props:['login_user_info','coupon_count','c24_point','cart_count'],
    computed:{
        isLogin(){
            return this.login_user_info.no > 0;
        },
        user_key(){
            return this.login_user_info.user_key;
        }
    },
    watch:{
        devmode_click(to){
            if(to > 3){
                if(localStorage.getItem('DEV') == 'Y'){
                    this.$emit('run_toast','2번 더 누르면 개발자모드 해제');

                }else{
                    this.$emit('run_toast','2번 더 누르면 개발자모드 전환');
                }
            }
            if(to >=5){
                eatalk.dev_mode();

                if(localStorage.getItem('DEV') == 'Y'){
                    this.$emit('run_toast','개발자모드 해제');
                }else{
                    this.$emit('run_toast','개발자모드 전환');
                }
                setTimeout(()=>{
                    location.reload();
                },1000);
            }
        }
    },
    methods: {
        show_membership(){
            this.showMembership = !this.showMembership;
        },
        goPage(url){
            this.$router.push(url);
        },
        logout(){
        	var chkConfirm = confirm('정말 로그아웃 하시겠습니까?');

        	if (chkConfirm) {
		        this.$emit('run_logout');
	        }
        },
        get_review_cnt(){
            var vm = this;

            var res = get_cache('my_page_review_cnt');
            if(res){
                vm.review_cnt = res.result.total;
                return ;
            }

            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/vod/review/my_list',
                data:{
                    key:vm.user_key
                },
                dataType: 'json',
                success: function(res, status) {
                    if(res.code==200) {
                        vm.review_cnt = res.result.total;
                        set_cache('my_page_review_cnt',res,10);

                    }else{
                        alert(res.message);
                    }
                }
            });
        },
        get_my_channel(){
            var vm = this;
            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/channel/json/my_detail',
                data:{
                    key:vm.user_key
                },
                dataType: 'json',
                success: function(res, status) {
                    if(res.code==200) {
                        // vm.init_load = true;
                        vm.my_data = res.result.data;
                    }else{
                        alert(res.message);
                    }
                }
            });
        },

        get_member(){
            var vm = this;
            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/user/customergroups',
                data:{
                    key:vm.user_key
                },
                dataType: 'json',
                success: function(res, status) {
                    if(res.code==200) {
                        for(var i=0; i<res.result.customergroups.length; i++){
                            if(res.result.customergroups[i].group_no != 1){
                                res.result.customergroups[i].points_information.amount_product = Math.floor(res.result.customergroups[i].points_information.amount_product)
                            }
                        }
                        vm.all_member = res.result.customergroups;
                    } else {
                        alert(res.message);
                    }
                }
            });
        },
    },
    activated() {
        window.scrollTo(0,0);
        clearInterval(this.initTimer);
        this.initTimer = setInterval(()=>{
            if(this.isLogin){
                clearInterval(this.initTimer);
                this.get_my_channel();
                this.get_review_cnt();
                this.get_member();
                this.member_1 = JSON.parse(sessionStorage.getItem('member_1'));
                if (this.member_1) {
                    this.group_name = this.member_1.data.group_name;
                    this.group_no = this.member_1.data.group_no;
                }
            }
        },100);
        this.$emit('lnb_last_pagh_set',this.$route.path);
    }
})
</script>

