		getDetail(no){
			var vm = this;
			var vno = no;
			if(!vno){
				vno = vm.vno;
			}

			if(!vno) return;

			return new Promise(function (resolve, reject){

				if (!vm.api_url) debugger;

				$.ajax({
					type: 'GET',
					url: vm.api_url,
					data:{
						video_id : vno,
						key:vm.user_key,
						pre_no:vm.$route.query.p,
					},
					dataType: 'json',
					success: function(res, status) {
						if(res.code==200) {
							vm.init_load = true;
                            /*if(res.result.data.block_info){
                                vm.init_load = false;
                                if(confirm('차단한 영상입니다. 재생하시겠습니까?')){
                                    vm.init_load = true;
                                }else{
                                    history.go(-1);
                                }
                            }*/
							vm.vod_data = res.result.data;
                            if(vm.vod_data.channel.state !='Y'){
                                vm.channel_ban = true;
                            }
							vm.vod_no = res.result.data.no;
							vm.is_like = res.result.data.like_yn;
							vm.is_hate = res.result.data.hate_yn;
							vm.is_product = res.result.data.product_info;
                            vm.comment_cnt = res.result.data.comment_cnt;
                            vm.link_change();
							vm.set_meta(res.result.data);

                            setTimeout(()=>{
                                vm.init_load_end = true;
