		link_change(){
			const urlRegex = /(\b(https?):\/\/[-A-Z0-9+&@#\/%?=~_|!:,.;]*[-A-Z0-9+&@#\/%=~_|])/gim;
			this.vod_data.content = this.vod_data.content.replace(urlRegex, '<a href="$1" target="_blank">$1</a>');
		},
		share : _.debounce(function (){
			this.vurl = 'https://eatalk.live24.app/go/';
			setTimeout(()=> {
                if(window.userAgent.indexOf('EatalkApp') > -1){
                    try{
                        //네이티브 로직
                        eatalkapp.share({title: this.vod_data.title, message: this.vod_data.sub_title, url: this.vurl + this.vod_data.video_id});
                    }catch(e){
                        //기존 로직
                        const t = document.createElement("textarea");
                        document.body.appendChild(t);
                        t.value = this.vurl + this.vod_data.video_id
                        t.select();
                        document.execCommand('copy');
                        document.body.removeChild(t);
                        sgr_common.toast('공유 URL을 복사하였습니다.');
                    }
                } else {
                    //기존 웹 로직
                    const t = document.createElement("textarea");
                    document.body.appendChild(t);
                    t.value = this.vurl + this.vod_data.video_id
                    t.select();
                    document.execCommand('copy');
                    document.body.removeChild(t);
                    sgr_common.toast('공유 URL을 복사하였습니다.');
                }
