                            dataType: 'json',
                        	success: function(res, status) {
                        		if(res.code==200){
                                    sessionStorage.setItem('is_block_vod','Y');
                                    sgr_common.toast(res.message);
                                    history.go(-1);
                        		}else{
                        		    alert(res.message);
                        		}
                        	},
                        	error: function(request,status,error) {
                        		console.log(request.responseText);
                        	}
                        });
                    }
                }
            });
        },
        unblock_pop(){

            var vm = this;
            vm.smenu_all_close();

            if(!this.isLogin){
                vm.login_chk();
                return;
            }

            var locale = {
                OK: 'I Suppose',
                CONFIRM: '차단하기',
                CANCEL: '취소',
