	mounted(){
        var vm = this;
        if (window.innerWidth >= 576) this.win_width = true;

		setTimeout(()=> {
			if (this.win_width) {
				var box = $('.video_intro').outerHeight();

				if (box > 65) {
					this.show_more = true;
					this.more = false;
					// 전체 높이를 못 가져와서 스타일에서 제거하고 추가함
					$('.video_intro').css({'-webkit-line-clamp': '3', 'height': '65px'});
				} else {
					this.show_more = false;
					this.more = true;
				}
			}

		}, 500)

        //document.body.addEventListener('click', vm.get_click_event);
    },
    beforeDestroy() {
        var vm = this;
        //document.body.removeEventListener('click', vm.get_click_event);
