	created(){
		this.vno = this.$route.params.no;
        if(!this.vno){
            if(this.play_id){
                this.vno = this.play_id;
            }
        }

		this.is_my = this.$route.query.my;
		this.api_url = this.is_my ? 'https://eatalk.live24.app/api/vod/json/my_detail' : 'https://eatalk.live24.app/api/vod/json/detail'
		this.getDetail().then(()=>{
			this.video_frame_set();
			this.get_comment();
			this.get_review_vod();
		});
	/*	window.addEventListener('resize',()=>{
			this.video_frame_set();
		})*/

        this.$emit('set_history_state');
	},
	destroyed() {
        $('body').css('overflow','');
		this.init_load = false;
		this.init_load_end = false;
        this.vod_data=[];
	}
})
