<script>
var pe_post_detail = Vue.component('pe_post_detail', {
    template: '#pe_post_detail_tem',
    name: 'pe_post_detail',
    components: ['cpt_mo_comments','cpt_titlebar','cpt_bottomsheet','cpt_pc_comments'],
    props:['login_user_info'],
    data(){
        return {
            post_detail: [],
            num : 0,
            init_load:false,
            is_mobile: false,
            comment_cnt : 0,
            comments: [],
            init_reply: false,
            comment_reply: false,
            vurl:'',
            title:'',
            show_product:false,
            s_frame:false,
            mode:'detail',
        }
    },
    watch:{
    },
    computed:{
        product_data(){
            return this.post_detail.product_info
        },
        sale(){
            if(this.product_data.retail_price > this.product_data.price){
                return this.roundts(this.product_data.price / this.product_data.retail_price * 100);
            }
        },
        price(){
            return this.roundts(this.product_data.price);
        },
        purl(){
            var inflow_id='';
            if(navigator.userAgent.indexOf('EatalkApp') > -1){
                inflow_id = 'eatalkapp';
            }

            if(this.mode == 'detail'){
                return '/product/detail.html?product_no='+this.product_data.product_no+'&ghost_mall_id='+inflow_id+'&ifrmode=true'
            }
            if(this.mode == 'pay'){
                return '/surl/O/'+this.product_data.product_no+'?ghost_mall_id='+inflow_id
            }
        },
        isLogin(){
            return this.login_user_info.no > 0;
        },
    },
    methods:{

        view_product(mode){
            var vm = this;
            this.mode = mode;
            this.show_product = true;
            setTimeout(()=>{
                this.s_frame = true;
            },200);
        },
        close_product(){
            var vm = this;
            this.s_frame = false;
            setTimeout(()=>{
                this.show_product = false;
            },200);
        },
        roundts ( val, ) {

            return Math.round(val );
        },
        /*****************************************************************
         *
         *  SGRSOFT Developer : nks150
         *  Date : 2021/10/12
         *  Description : api 호출
         *
         *****************************************************************/
        post_detail_list(){
            var vm = this;
            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/post/json/detail',
                data:{
                    no : vm.num,
                },
                success: function(res, status) {
                    if(res.code==200) {
                        vm.init_load = false;
                        res.result.data.created = res.result.data.created.substr(0,10).replace(/-/g,'.');
                        vm.post_detail = res.result.data;
                        vm.title = res.result.data.category_info.title
                    }else{
	                    bootbox.alert(res.message);
                    }
                }
            });
        },
        get_comment() {
            var vm = this;

            $.ajax({
                type: "GET",
                url: 'https://eatalk.live24.app/api/comment/json/list',
                data: {
                    sch_type: 'post',
                    sch_target_no: vm.num,
                    page_no: 1,
                    row_count : 1000,
                },
                success: function (res, status) {
                    for(var i=0; i<res.result.data.length; i++){
                        res.result.data[i].is_menu_view = false;
                    }
                    vm.comments = res.result.data;
                    vm.comment_cnt = res.result.data.length;
                },
                error: function (request, status, error) {
                    console.log(request.responseText);
                },
            });
        },
        mobile_chk(){
            if(/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)){
                this.is_mobile = true;
                this.view_viedo = 3
            }
        },
        open_comment() {
            this.get_comment();
            this.$refs.flag_reply.flag_reply();
            this.init_reply = false;
            sgr_custom.commentBox.open(".view_comment");
            var $box = $('.view_comment');
            calcH = "calc(100% - "+ $('#video1').height() + "px)";
            $box.css("height",calcH);
        },
        close_comment() {
            this.$refs.flag_reply.flag_reply();
            sgr_custom.commentBox.close(".view_comment");
        },
        reply_chk(data){
            this.comment_reply = data;
        },
        comment_back(){
            this.comment_reply = false;
            this.$refs.flag_reply.flag_reply();
        },
        share(){
            this.vurl = window.document.location.href;
            setTimeout(()=> {

                const t = document.createElement("textarea");
                document.body.appendChild(t);
                t.value = this.vurl;
                t.select();
                document.execCommand('copy');
                document.body.removeChild(t);
                sgr_common.toast('공유 URL을 복사하였습니다.');
            },300)
        },
        run_login(){
            this.$emit('run_login');
        },
    },
    deactivated() {
        this.scroll = document.documentElement.scrollTop;
    },
    activated() {
        window.scrollTo(0,this.scroll)
    },
    created() {
        window.scrollTo(0,0);
        this.num = this.$route.query.no;
        this.post_detail_list();
        this.get_comment();

    },
    mounted(){
        this.mobile_chk();
    },
})
</script>

