<script>
var pe_search = Vue.component('pe_search', {
	template: '#pe_search_tem',
	data () {
		return {
            vod_list:[],
            scroll:0,
            page_no:1,
            row_count:8,
            is_scroll:true,
            init_load:false,
            total:0,
            toDos:[],
            savedtodos:[],
            show_todos:true,
            width:0,
        }
	},
    props:['login_user_info'],
    computed:{
        mobile(){
            if(this.width < 800){
                return true
            }else if(this.width >= 800){
                return false
            }
        },
        is_mobile(){
            return /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
        },
        last_path(){
            /*if(sessionStorage.getItem('last_router_path')){
                return sessionStorage.getItem('last_router_path');
            }else{

            }*/
            return '/';
        },
        user_key(){
            return this.login_user_info.user_key;
        }
    },
	methods: {
        clickSearch(txt){
            $('#r_keyword').val(txt);
            this.search_run();
        },
        allDeleteToDo() { //전체 item을 삭제
            localStorage.clear('todos');
            this.toDos = [];
        },
        deleteToDo(id) { //각각의 item을 삭제
            this.toDos = this.toDos.filter((toDo) => toDo.id !== parseInt(id));
            this.saveToDos();
        },
	    saveToDos(){
            typeof(Storage) !== 'undefined' && localStorage.setItem("todos", JSON.stringify(this.toDos));
        },
        stacked(){
            var newTodoItem = $('#r_keyword').val();
            if(this.toDos.filter((x)=>x.text == newTodoItem).length == 0) {
                if (newTodoItem != '') {
                    var newTodoObj = {
                        id: Date.now(),
                        text: newTodoItem
                    };
                    this.toDos.push(newTodoObj);
                    this.saveToDos();
                }
            }
        },
        search_run(){
            this.stacked();
            if(this.$route.query.keyword != $('#r_keyword').val()){
                this.$router.replace({path:'/search',query:{keyword : $('#r_keyword').val() } })
            }
        },
        /*****************************************************************
        *
        *  SGRSOFT Developer : now17
        *  Date : 2021/10/25
        *  Description : 검색어 저장
        *
        *****************************************************************/
        save_search_keyword(){
            var vm = this;
            if(this.$route.query.keyword){
                $.ajax({
                    type: 'POST',
                    url: 'https://eatalk.live24.app/api/search/json/add_keyword',
                    data: {
                        db_keyword: vm.$route.query.keyword,
                        key:vm.user_key
                    },
                    dataType: 'json',
                    success: function(res, status) {

                    },
                    error: function(request, status, error) {
                        console.log(request.responseText);
                    }
                });
            }
        },
        /*****************************************************************
        *
        *  SGRSOFT Developer : now17
        *  Date : 2021/10/20
        *  Description : 검색 하기
        *
        *****************************************************************/
        get_vod(){
            var vm = this;
            if(this.$route.query.keyword){
                this.init_load = false;
                // this.save_search_keyword()

                $.ajax({
                    type: 'GET',
                    url: 'https://eatalk.live24.app/api/vod/json/list',
                    data:{
                        search:vm.$route.query.keyword,
                        row_count : vm.row_count,
                        page_no: vm.page_no,
                    },
                    dataType: 'json',
                	success: function(res, status) {
                		if(res.code==200){
                            vm.init_load = true;
                            if(res.result && res.result.total){
                                vm.vod_list.push(...res.result.data);
                                vm.total = res.result.total;

                                if(vm.row_count > res.result_cnt){
                                    vm.is_scroll = false;
                                }else{
                                    vm.is_scroll = true;
                                }
                            }
                		}else{
                		    console.log(res.message);
                		}
                	},
                	error: function(request,status,error) {
                		console.log(request.responseText);
                	}
                });
            }
        },
        last_scroll(){
            // console.log('마지막 스크롤')
            this.is_scroll = false;
            this.page_no++;
            this.get_vod();
        },
    },
	created () {
        this.width = $(window).width();
        window.addEventListener('resize',()=>{
            this.width = $(window).width();
        });
        if(this.$route.query.keyword){
            this.show_todos = false;
        }
        this.get_vod();
	},
    mounted() {
        if(localStorage.getItem("todos")){
            this.savedtodos = JSON.parse(localStorage.getItem('todos'))
            this.toDos = this.savedtodos;
        }
    },
    destroyed() {
        this.$store.commit('search_clear', true);
    },
})
</script>
