<script>
var pe_channel_setting = Vue.component('pe_channel_setting', {
	template: '#pe_channel_setting_tem',
	components: ['cpt_webeditor','cpt_channel_banner','cpt_thumb_only','cpt_back_btn'],
	props: ['login_user_info'],
    computed:{
        isLogin(){
            return !!this.login_user_info.no
        },
        user_key(){
            return this.login_user_info.user_key;
        }
    },
	data () {
		return {
			info: '',
			init_load: false,
            channel_data: [],
            channel_ban: false,

		}
	},
	methods: {
		async getInfo() {
			var vm = this;

			await $.ajax({
				type: 'GET',
				url: 'https://eatalk.live24.app/api/channel/json/my_detail',
				data: {
					key: vm.user_key,
				},
				dataType: 'json',
				success(res, status) {
				    if(res.code === 200){
                        vm.info = res.result.data;
                    }
				},
				error: function(request,status,error) {
					console.log(request.responseText);
				}
			});
		},
		img_upload_s3(arg,no_resize) {
			var vm = this;
			var fd = new FormData();
			fd.append(arg, document.getElementById(arg).files[0]);
			fd.append('field_name', arg);
			fd.append('file_dir', 'eatalk');
            if(no_resize){
                fd.append('no_resize', 'Y');
            }

			$.ajax({
				type: 'POST',
				url: 'https://eatalk.live24.app/api/file/s3_img_upload',
				data: fd,
				processData: false,
				contentType: false,
				dataType: 'json',
				success: function(res, status) {
					if(res.code==200) {
						if(res.result.filename){
							if (arg == 'channel_image_url') {
								vm.info.channel_image_url = res.result.filename;
							}
							if (arg == 'channel_profile_img') {
								vm.info.channel_profile_img = res.result.filename;
							}
						}
					}else{
                        sgr_common.toast('파일 업로드에 실패하였습니다.');
					}
				},
				error: function(request,status,error) {
                    sgr_common.toast('파일 업로드에 실패하였습니다.');
				}
			});
		},
		editor_callback(text) {
			this.info.info_html = text;
		},
		save() {
			var vm = this;
			var data = {
				db_name: this.info.name,
				db_channel_image_url: this.info.channel_image_url,
				db_channel_profile_img: this.info.channel_profile_img,
				db_info_html: this.info.info_html,
                db_sub_text: this.info.sub_text,
			}
			var url = 'https://eatalk.live24.app/api/channel/json/edit';

			if (this.info.type == 'seller') {
				data = {...data, no: this.info.no };
				url = 'https://eatalk.live24.app/api/channel/json/mng_edit';
			}

			$.ajax({
				type: 'POST',
				url: url,
				data: data,
				dataType: 'json',
				success(res, status) {
                    sgr_common.toast('채널 정보가 수정되었습니다.');
					vm.getInfo();
					vm.$router.push({ path: '/channel/detail/'+vm.login_user_info.channel_no});
                    setTimeout(()=>{
	                    location.reload();
                    },500)
				},
				error: function(request,status,error) {
					console.log(request.responseText);
				}
			});
		},
        getMyChannelData(){
            var vm = this;
            return new Promise(function(resolve, reject){
                $.ajax({
                    type: 'GET',
                    url: 'https://eatalk.live24.app/api/channel/json/my_detail',
                    data:{
                        key:vm.user_key
                    },
                    dataType: 'json',
                    success: function(res, status) {
                        if(res.code==200) {
                            //console.log(res)

                            vm.channel_data = res.result.data;
                            if(vm.channel_data.state !='Y'){
                                vm.channel_ban = true;
                            }
                            resolve();

                        }else{
                            //alert(res.message);
                            reject(new Error(res.message));
                        }

                        setTimeout(()=>{
                            vm.init_load_end = true
                        },300)
                    }
                });
            });
        },
	},
	mounted () {
	    const vm = this;
        this.getMyChannelData().then(()=> {
            setTimeout(function() {
                vm.getInfo();
            }, 100)
        });
	}
})
</script>

