var pe_channel = Vue.component('pe_channel', {
    template: '#pe_channel_tem',
    name: 'pe_channel',
    components:['cpt_tabbar','cpt_back_btn','cpt_channel_banner','cpt_thumb_only','cpt_video_col_card'],
    data(){
        return {
            is_my : false,
            init_load:false,
            init_load_end:false,
            vod_init_load:false,
            tab_type:0,
            scroll:0,
            channel_no:'',
	        channel_data: {},
	        video_list: [],
	        page_no: 1,
	        channel_info: {},
	        writeState: false,
            subscribe_yn : false,
            no_channel: false,
            is_scroll:true,
            channel_ban:false,
            row_count : 30,
            width:0,
        }
    },
    props:['login_user_info'],
    watch:{
        login_user_info(newVal,oldVal){
            if(newVal.channel_no != oldVal.channel_no){
                this.init();
                // console.log('mjjeong watch 채널 로그인 정보 oldVal ',newVal.channel_no)
            }
        }
    },
    computed:{
        is_channel_image_view(){
            if(!this.channel_data.channel_image_url){
                if(this.width > 800){
                    return false;
                }else{
                    return true;
                }
            }else{
                return true;
            }
        },
        isLogin(){
            return this.login_user_info.no > 0;
        },
        user_key(){
            return this.login_user_info.user_key;
        },
        is_mobile(){
            return /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
        },
    },
    methods:{
        nl2br(text){
            return text.replace(/\n/g, '<br>');
        },
	    goBack(){
		   if(history.length == 1){
			    this.$router.push('/');
		    }else{
			    this.$router.go(-1);
		    }
	    },
        go_page(page){
            this.$router.push(page)
        },
        last_scroll(){
            if(!this.is_scroll){
                return;
            }
            this.is_scroll = false;
            this.page_no++;
            this.getVideoList();
        },
        selTab(type){
            this.tab_type = type;
        },
	    getMyChannelData(){
            var vm = this;
            return new Promise(function(resolve, reject){
                $.ajax({
                    type: 'GET',
                    url: 'https://eatalk.live24.app/api/channel/json/my_detail',
                    data:{
                        key:vm.user_key
                    },
                    dataType: 'json',
                    success: function(res, status) {
                        if(res.code==200) {
                             //console.log(res)
                            vm.init_load = true;
                            vm.channel_data = res.result.data;
                            if(vm.channel_data.state !='Y'){
                                vm.channel_ban = true;
                            }
                            resolve();

                        }else{
                            //alert(res.message);
                            reject(new Error(res.message));
                        }

                        setTimeout(()=>{
                            vm.init_load_end = true
                        },300)
                    }
                });
            });
        },

        getChannelData(){
            var vm = this;
            return new Promise(function(resolve, reject){
                $.ajax({
                    type: 'GET',
                    url: 'https://eatalk.live24.app/api/channel/json/detail',
                    data:{
                        no : vm.channel_no,
                    },
                    dataType: 'json',
                    success: function(res, status) {

                        if(res.result.data) {
                            vm.init_load = true;
                            vm.channel_data = res.result.data;
                            if(vm.channel_data.state !='Y'){
                                vm.channel_ban = true;
                            }
                            vm.subscribe_yn = res.result.data.subscribe_yn;
                            resolve(res.result.data);
                        }else{

                            vm.no_channel = true;
                            reject(res.message);
                        }
                        setTimeout(()=>{
                            vm.init_load_end = true
                        },300)
                    },
                    error: function(request,status,error) {
                        vm.no_channel = true;
                        reject(res.message);
                    }
                });
            });
        },
        getVideoList(){
            var vm = this;
            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/vod/json/list',
                data:{
                    row_count : this.row_count,
                    page_no: this.page_no,
                    sch_channel_no:vm.channel_no
                },
                dataType: 'json',
                success: function(res, status) {
                    if (res.code == 200) {
                        vm.init_load = true;
                        vm.vod_init_load = true;
                        vm.video_list.push(...res.result.data);
                        vm.is_scroll = vm.row_count <= res.result.data.length;
                    }
                }
            });
        },
        login_chk(){
            var vm = this;
            sgr_custom.confirm('로그인 후 이용하실 수 있습니다.\n<br>로그인 하시겠습니까?',{
                ycb(){
                    vm.$emit('run_login');
                },
                ybt:'예',
                nbt:'아니오'
            });
        },
	    getWrite: _.debounce(function (){

            if(!this.isLogin){
                this.login_chk();
                return;
            }
		    var vm = this;

		    $.ajax({
		        type: 'POST',
		        url: 'https://eatalk.live24.app/api/subscribe/json/write',
		        data: {
			        db_channel_no: this.channel_no
		        },
			    success: function(res, status) {
			        if(res.code==200) {
                        vm.subscribe_yn = !vm.subscribe_yn;
                        var msg = vm.subscribe_yn ? '구독이 설정되었습니다.' : '구독이 해지되었습니다.'
                        sgr_common.toast(msg);

                        //fixed nks150 : 채널 구독자리스트 갱신
                        if (vm.is_my){
                            vm.getMyChannelData()
                        }else{
                            vm.getChannelData()
                        }
                        //fixed now17 : lnb 구독리스트 갱신
                        if(!vm.is_mobile){
                            vm.$emit('lnb_submenu_reload');
                        }
			        }else{
				        alert(res.message);
			        }
		        }
	        })
	    },300),
        report_pop(){
            if(!this.isLogin){
                this.login_chk();
                return;
            }
            var vm = this;
            var locale = {
                OK: 'I Suppose',
                CONFIRM: '신고하기',
                CANCEL: '취소',
            };

            bootbox.addLocale('custom', locale);
            if(!this.isLogin){
                bootbox.alert('로그인이 필요합니다.')
            }else {
                bootbox.prompt({
                    placeholder: '신고 사유를 입력해주세요',
                    title: "채널 신고",
                    inputType: 'textarea',
                    locale: 'custom',
                    callback: function (result) {
                        if (result) {
                            vm.add_report(result);
                        }
                    }
                });
            }
        },
        add_report(data){
            var vm = this;

            $.ajax({
                type: 'POST',
                url: 'https://eatalk.live24.app/api/report/json/write',
                data:{
                    key : vm.key,
                    type : 'channel',
                    target_no : vm.channel_no,
                    db_report_txt : data
                },
                dataType: 'json',
                success: function(res, status) {
                    if(res.code==200){
                        var msg = "[" + vm.channel_data.name + "] 채널이 신고 완료 되었습니다.";
                        sgr_common.toast(msg);
                    }else{
                        alert(res.message);
                    }
                },
                error: function(request,status,error) {
                    console.log(request.responseText);
                }
            });
        },
        init(){
            if(this.channel_no === this.login_user_info.channel_no){
                this.is_my = true;
            }


            if (this.is_my){
                this.getMyChannelData().catch((err) => {
                    if (err.message.indexOf('로그인') > -1) {
                        this.$emit('run_login');

                    }
                });
                this.$emit('lnb_last_pagh_set',this.$route.path);
            }else{
                this.getChannelData();
            }
        }
    },

    created(){
        window.scrollTo(0,0);
        this.width = $(window).width();
        window.addEventListener('resize',()=>{
            this.width = $(window).width();
        });

        this.channel_no = this.$route.params.id;

        this.getVideoList();

        this.init();
    },
    mounted(){
        if($(window).width() > 800){
            this.init_load_end= true;
        }
    }
})
</script>

