<script>
var pe_channel_select = Vue.component('pe_channel_select', {
	template: '#pe_channel_select_tem',
	components: ['cpt_titlebar','cpt_thumb_only'],
	data () {
		return {
			channel: [],
			select: null,
            row_count : 100
		}
	},
	props: ['my_channel_data','login_user_info'],
    computed:{
        user_key(){
            return this.login_user_info.user_key;
        },
    },
	methods: {
		getInfo() {
			var vm = this;
			
			$.ajax({
				type: 'GET',
				url: 'https://eatalk.live24.app/api/channel/json/my_detail',
				data: {
					key: vm.user_key,
				},
				dataType: 'json',
				success(res, status) {
					if(res.code==200) {
						vm.select = res.result.data.no;
					}
				},
				error: function(request,status,error) {
					console.log(request.responseText);
				}
			});
		},
		get_channel() {
			var vm = this;
			
			$.ajax({
				type: 'POST',
				url: 'https://eatalk.live24.app/api/channel/json/mng_list',
				data: {
					key: vm.user_key,
                    row_count : this.row_count
				},
				dataType: 'json',
				success: function(res, status) {
					if(res.code==200) {
						vm.channel = res.result.data;
					}
				}
			});
		},
		selected_channel(data) {
			var vm = this;

