<script>

var pe_history = Vue.component('pe_history', {
	template: '#pe_history_tem',
	components: ['cpt_titlebar','cpt_video_card','cpt_infinity_scroll'],
    props:['login_user_info'],
    computed:{
        isLogin(){
            return this.login_user_info.no > 0;
        },
        user_key(){
            return this.login_user_info.user_key;
        }
    },
    watch:{
        isLogin(to){
            if(to){
                this.init_run();
            }
        }
    },
	data () {
		return {
			vod_list:[],
			is_scroll:true,
            row_count:30,
            page_no:1,
            init_load:false,
		}
	},
	methods: {
		last_scroll(){
			this.is_scroll = false;
			this.page_no++;
			this.get_vod();
		},
		goList(){
			this.$router.push('/channel/list/recommand')
		},
        get_vod: _.debounce(function() {
			var vm = this;
			$.ajax({
				type: 'GET',
				url: 'https://eatalk.live24.app/api/history/json/vod_list',
				data:{
					row_count : this.row_count,
					page_no: this.page_no,
                    order_col:'updated',
                    order_type:'desc',
                    key:vm.user_key,
				},
				dataType: 'json',
				success: function(res, status) {
					if(res.code==200) {
						vm.init_load = true;
						for(var i=0; i<res.result.data.length; i++){
							var tmp_object = res.result.data[i];

							// 삭제 된 데이터가 내려와서 필터 추가
							if (tmp_object.vod_info.no) {
								//tmp_object.channel = {};
								tmp_object.channel.name = tmp_object.channel.name;
								tmp_object.title = tmp_object.vod_info.title;
								tmp_object.timeago = tmp_object.vod_info.timeago;
								tmp_object.duration_txt = tmp_object.vod_info.duration_txt;
								tmp_object.thumb_url = tmp_object.vod_info.thumb_url;
								tmp_object.thumb_sd_url = tmp_object.vod_info.thumb_sd_url;
								tmp_object.thumb_small_url = tmp_object.vod_info.thumb_small_url;
								tmp_object.no = tmp_object.vod_info.no;
								tmp_object.video_id = tmp_object.vod_info.video_id;
								tmp_object.thumb_sd = tmp_object.vod_info.thumb_sd;
								try{
                                   tmp_object.block_info = tmp_object.vod_info.block_info;
                                }catch(e){

                                }
								vm.vod_list.push(tmp_object);
							}
                        }
						// vm.vod_list.push(...res.result.data);
						if(vm.row_count > res.result.data.length){
							vm.is_scroll = false;
						}else{
							vm.is_scroll = true;
						}
					}else{
					    // console.log(res.message);
						//alert(res.message);
					}
				}
			});
		}),
		deleteList() {
			var vm = this;

            bootbox.confirm({
                message: "정말 삭제하시겠습니까?",
                buttons: {
                    confirm: {
                        label: '삭제',
                        className: 'btn btn-md btn-primary'
                    },
                    cancel: {
                        label: '취소',
                        className: 'btn btn-md b'
                    }
                },
                callback: function (result) {
                    if(result){
                        $.ajax({
                            type: 'GET',
                            url: 'https://eatalk.live24.app/api/history/json/vod_del_all',
                            data: {
                                key:vm.user_key,
                            },
                            dataType: 'json',
                            success: function(res, status) {
                                if (res.code == 200) {
                                    //todo joo: 시청기록 쌓이면 수정해주기
                                    vm.vod_list = []
                                }
                            }
                        });
                    }
                }
            });
		},
        init_run(){
            window.scrollTo(0,0);
            if(this.isLogin){
                this.get_vod();
            }
        }
	},
	deactivated() {
		this.scroll = document.documentElement.scrollTop;
	},
	activated() {
		window.scrollTo(0,this.scroll)
        this.$emit('lnb_last_pagh_set',this.$route.path);
	},
	created(){
		this.init_run();
	},
})
</script>
