<script>
var pe_my_video = Vue.component('pe_my_video', {
    template: '#pe_my_video_tem',
    components: ['cpt_titlebar','cpt_video_card','cpt_infinity_scroll'],
    props:['login_user_info'],
    computed:{
        isLogin(){
            return this.login_user_info.no > 0;
        },
        user_key(){
            return this.login_user_info.user_key;
        }
    },
    data () {
        return {
            vod_list:[],
            is_scroll:true,
            row_count:10,
            page_no:1,
            init_load:false,
            interval_vod : '',
        }
    },
    methods: {
        edit_vod(list){
            this.$router.push({path : '/video/upload/'+list.video_id});
        },
        del_vod(no){
            var vm = this;
            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/vod/json/del',
                data:{
                    no: no
                },
                dataType: 'json',
                success: function(res, status) {
                    if(res.code==200) {
                        setTimeout(function () {
                            location.reload();
                        },1500)
                        sgr_common.toast('영상이 삭제 되었습니다.');
                    }else{

                    }
                }
            });
        },
        last_scroll(){
            this.is_scroll = false;
            this.page_no++;
            this.ge_vod();
            // console.log('마지막페이지');
        },
        ge_vod: _.debounce(function() {
            var vm = this;
            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/vod/json/my_list',
                data:{
                    key : vm.user_key,
                    row_count : 10,
                    sch_channel_no: this.login_user_info.channel_no,
                    page_no: this.page_no
                },
                dataType: 'json',
                success: function(res, status) {
                    if(res.code==200) {
                        vm.init_load = true;
                        vm.vod_list.push(...res.result.data);

                        if(vm.page_no === 1){
                            for(var value of vm.vod_list){
                                if(value.incoding_state !== 'COMPLETE'){
                                    vm.set_interval();
                                    break;
                                }
                            }
                        }

                        vm.is_scroll = vm.row_count <= res.result_cnt;
                    }else{
                        //error
                    }
                }
            });
        }),
        //첫번째 페이지에 있는 인코딩중인 영상 업데이트를 위한 메소드
        ge_vod_re: _.debounce(function() {
            var vm = this;
            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/vod/json/my_list',
                data:{
                    key : vm.user_key,
                    sch_channel_no: this.login_user_info.channel_no,
                    row_count : 10,
                    page_no: 1
                },
                dataType: 'json',
                success: function(res, status) {
                    if(res.code==200) {
                        vm.init_load = true;
                        var check_cnt = 0;

                        for(var key in res.result.data){
                            if(res.result.data[key].incoding_state !== 'COMPLETE'){
                                check_cnt++;
                            }
                        }

                        if(check_cnt<1){
                            for(var i=0; i<res.result_cnt; i++){
                                vm.vod_list[i] = res.result.data[i];
                                // vm.vod_list[i].incoding_state = 'COMPLETE'
                            }
                            vm.$forceUpdate();
                            vm.del_interval();
                        }
                    }else{
                        //error
                    }
                }
            });
        }),
        deleteList() {
            var vm = this;

            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/history/json/vod_del_all',
                data: {
                    key: vm.user_key,
                },
                dataType: 'json',
                success: function(res, status) {
                    if (res.code == 200) {
                        //todo joo: 시청기록 쌓이면 수정해주기
                        vm.vod_list = []
                    }
                }
            });
        },
        set_interval(){
            const vm = this;
            this.interval_vod = setInterval(()=>{
                vm.ge_vod_re();
            },10000)

        },
        del_interval(){
            clearInterval(this.interval_vod)
        }

    },
    deactivated() {
        this.scroll = document.documentElement.scrollTop;
    },
    activated() {
        window.scrollTo(0,this.scroll)
    },
    created(){
        window.scrollTo(0,0);
        this.initTimer = setInterval(()=>{
            if(this.isLogin){
                this.ge_vod();
                clearInterval(this.initTimer);
            }
        },100);
    },
})
</script>
