		},
		user_key(){
			return this.login_user_info.user_key;
		}
	},
    methods: {
        write_list_last_scroll(){
            if(this.write_list_is_scroll == false){
                return;
            }
            this.write_list_is_scroll = false;
            this.write_list_page_no++;
            this.get_write_list();
        },
        product_list_last_scroll(){
            if(this.product_list_is_scroll == false){
                return;
            }
		    this.product_list_is_scroll = false;
		    this.product_list_page_no++;
            this.get_list();
	    },
	    sel_tab(type){
		    this.tab_type = type;
	    },
	    get_list() {
	    	var vm = this;

		    vm.init_load = true;
	    	$.ajax({
			    type: 'GET',
			    url: 'https://eatalk.live24.app/api/order/json/my_order_products',
			    data: {
				    key: vm.user_key,
				    page_no: this.product_list_page_no,
				    row_count: this.row_count
			    },
			    dataType: 'json',
			    success: function(res, status) {
			    	if (res.code == 200) {
					    // console.log(res)
					    vm.init_load = false;
					    if (res.result.data != null) {
						    vm.list.push(...res.result.data);
					    }
					    if(vm.row_count > res.result_cnt){
						    vm.product_list_is_scroll = false;
					    }else{
						    vm.product_list_is_scroll = true;
					    }
				    }
			    }
		    });
	    },
	    get_order_chk(data) {
		    var vm = this;

		    vm.init_load = true;
		    $.ajax({
			    type: 'POST',
			    url: 'https://eatalk.live24.app/api/vod/review/order_chk',
			    data: {
				    key: vm.user_key,
				    order_no: data.order_id
			    },
			    dataType: 'json',
			    success: function(res, status) {
				    // console.log(res)
				    if (res.code == 200) {
					    vm.$router.push({path: '/video/upload/review/'+data.order_product_no});
				    } else {
					    bootbox.alert(res.message);
				    }
			    }
		    });
	    },
	    get_write_list() {
	    	var vm = this;

		    vm.init_load = true;
	    	$.ajax({
			    type: 'GET',
			    url: 'https://eatalk.live24.app/api/vod/review/my_list',
			    data: {
				    key: vm.user_key,
				    page_no: this.write_list_page_no,
				    row_count: this.row_count
			    },
			    dataType: 'json',
			    success: function(res, status) {
				    if (res.code == 200) {
					    // console.log(res)
                        vm.vod_init_load = true;
                        vm.init_load = false;
                        if (res.result.data) {
						    for(var i=0; i<res.result.data.length; i++){
							    var temp_obj = res.result.data[i];

							    temp_obj.product = {};
							    temp_obj.product.list_image = temp_obj.product_info.list_image;
							    temp_obj.product.product_name = temp_obj.product_info.product_name;
							    vm.write_list.push(temp_obj);
						    }
                        }
                        if(vm.write_list_page_no === 1){
                            for(var value of vm.write_list){
                                if(value.vod_info.incoding_state !== 'COMPLETE'){
                                    vm.set_interval();
                                    break;
                                }
                            }
                        }

                        if(vm.row_count > res.result_cnt){
                            vm.write_list_is_scroll = false;
                        }else{
                            vm.write_list_is_scroll = true;
                        }
				    }
			    }
		    });
	    },
        get_write_list_re() {
            var vm = this;

            vm.init_load = true;
            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/vod/review/my_list',
                data: {
                    key: vm.user_key,
                    page_no: this.write_list_page_no,
                    row_count: this.row_count
                },
                dataType: 'json',
                success: function(res, status) {
                    if (res.code == 200) {
                        // console.log(res)
                        vm.vod_init_load = true;
                        vm.init_load = false;
                        var check_cnt = 0;

                        if (res.result.data) {
                            for(var i=0; i<res.result.data.length; i++){
                                var temp_obj = res.result.data[i];

                                if(temp_obj.vod_info.incoding_state !== 'COMPLETE'){
                                    check_cnt++;
                                }
                                temp_obj.product = {};
                                temp_obj.product.list_image = temp_obj.product_info.list_image;
                                temp_obj.product.product_name = temp_obj.product_info.product_name;
                                vm.write_list.push(temp_obj);
                            }
                        }

                        if(check_cnt<1){
                            for(var i=0; i<res.result_cnt; i++){
                                vm.write_list[i] = res.result.data[i];
                            }
                            vm.del_interval();
                        }

                        if(vm.row_count > res.result_cnt){
                            vm.write_list_is_scroll = false;
                        }else{
                             vm.write_list_is_scroll = true;
                        }
                    }
                }
            });
        },
	    detail_video(data) {
		    var vm = this;

		    if(data.incoding_state === 'WAITING'){
			    return;
		    }

		    var path = this.is_my_vod ? '/video/' + data.video_id+'?my=true' : '/video/detail/' + data.video_id;

            vm.$router.push({ path: path });
            // vm.$router.push({ path: '/video/' + data.video_id , });
	    },
	    click_modify (data) {
		    this.$router.push({ path : '/video/upload/review/modify/'+data.no });
	    },
	    click_delete (no) {
	    	var vm = this;

		    bootbox.confirm({
			    message: "정말 삭제하시겠습니까?",
			    buttons: {
				    confirm: {
					    label: '삭제',
					    className: 'btn btn-sm btn-primary'
				    },
				    cancel: {
					    label: '취소',
					    className: 'btn btn-sm b'
				    }
			    },
			    callback: function (result) {
				    if(result){
				    	console.log(result)
					    $.ajax({
						    type: 'POST',
						    url: 'https://eatalk.live24.app/api/vod/review/del',
						    data: {
						    	key: vm.user_key,
							    no: no
						    },
						    dataType: 'json',
						    success: function(res, status) {
							    if (res.code==200) {
                                    sgr_common.toast('삭제되었습니다.');
								    vm.get_list();
								    vm.get_write_list();
							    }
						    }
					    });
				    }
			    }
		    });
	    },
        set_interval(){
            const vm = this;
            this.interval_write_list = setInterval(()=>{
                vm.get_write_list_re();
            },10000)

        },
        del_interval(){
            clearInterval(this.interval_write_list)
        },
    },
    mounted(){
	    var vm = this;

	    vm.get_list();
	    vm.get_write_list();
    },
})
</script>

