<script>
var pe_review_video_write = Vue.component('pe_review_video_write', {
	template: '#pe_review_video_write_tem',
	components:['cpt_titlebar','cpt_vod_upload','cpt_webeditor'],
	props:['login_user_info'],
    computed:{
        isLogin(){
            return this.login_user_info.no > 0;
        },
        user_key(){
            return this.login_user_info.user_key;
        }
    },
	data(){
		return {
			title:'',
			sub_title:'',
			content:'',
			upload_vod:'',
			upload_thumb:'',
			upload_percent: 0,
			chk_state: false,
			submit_ing: false,
			page_edit: false,
			api_url: '',
			detail_info: {},
			no: this.$route.params.no,
		}
	},
	methods:{
		editor_callback(text) {
			this.content = text;
		},
		get_detail() {
			var vm = this;

			$.ajax({
				type: 'GET',
				url: 'https://eatalk.live24.app/api/vod/review/detail',
				data: {
					key: vm.user_key,
					no: this.no
				},
				dataType: 'json',
				success: function(res, status) {
					if (res.code == 200) {
						vm.title = res.result.data.vod_info.title;
						vm.sub_title = res.result.data.vod_info.sub_title;
						vm.content = res.result.data.vod_info.content;
						if (res.result.data.vod_info.content == 'null') {
							vm.content = ''
						}
						vm.upload_thumb = res.result.data.vod_info.thumb_url;
						vm.upload_vod = res.result.data.vod_info.video_id+'.mp4';
					}else{
						alert(res.message);
					}
				}
			});
		},
		vod_upload(){
			var vm = this;
			var fd = new FormData();

			// value check
			if(!this.title){
				bootbox.alert('제목을 입력해주세요');
				return
			}else if(!this.page_edit && !document.getElementById('upload_vod').files[0]){
				bootbox.alert('영상을 업로드 해주세요');
				return
			}else if(!this.page_edit && !this.chk_state){
				bootbox.alert('동의해주세요.');
				return
			}

			vm.submit_ing = true;

			fd.append('vod_file', document.getElementById('upload_vod').files[0]);
			fd.append('db_thumb_url', vm.upload_thumb);
			fd.append('db_content', vm.content);
			fd.append('db_title', vm.title);
			fd.append('db_sub_title' , vm.sub_title);
			fd.append('key', vm.user_key);
            fd.append('db_type', 'review');
			if (this.page_edit) {
				fd.append('no', this.no);
			} else {
				fd.append('order_product_no', this.no);
			}

			vm.upload_percent = 0;

			$.ajax({
				type: 'POST',
				url: this.api_url,
				data: fd,
				processData: false,
				contentType: false,
				dataType: 'json',
				xhr: function() { //XMLHttpRequest 재정의 가능
					var xhr = $.ajaxSettings.xhr();
					xhr.upload.onprogress = function(e) { //progress 이벤트 리스너 추가
						var percent = e.loaded * 100 / e.total;
						if(percent>98){
							setTimeout(()=> {
								vm.upload_percent = percent;
							},200);
						}else {
							vm.upload_percent = percent;
						}
					};
					return xhr;
				},
				success: function (res, status) {
					// console.log(data);
					vm.submit_ing = false;
					if (res.code == 200) {
					    let msg = !vm.page_edit ? '등록 되었습니다.' : '수정되었습니다.'
                        sgr_common.toast(msg);
						vm.$router.push('/video/review');
					} else if (res.code == 501) {
						alert(`관리자에 문의하세요.\n[${res.code}] ${res.message}`);
					} else {
						alert(`알림.\n[${res.code}] ${res.message}`);
					}
					vm.upload_ing = false;
				},
				error: function (request, status, error) {
					vm.submit_ing = false;
				}
			});
		},
		ncp_image_uploader(file) {
			var vm = this;
			var data = new FormData();
				data.append("file", file.files[0]);
				data.append('field_name', 'file');
				data.append('file_dir', 'images');

			$.ajax({
				data : data,
				type : "POST",
				url : "https://eatalk.live24.app/api/file/s3_img_upload",
				contentType : false,
				processData : false,
				success : function(res) {
                    if(res.code == 200){
                        vm.upload_thumb = res.result.filename;
                    }else{
                        sgr_common.toast(res.message);
                    }
                }
			});
		},
		cancel() {
			this.$router.push('/video/review');
		}
	},
	created() {
		this.page_edit = this.$route.fullPath.indexOf('modify') != -1;
		this.api_url = this.page_edit ? 'https://eatalk.live24.app/api/vod/review/edit' : 'https://eatalk.live24.app/api/vod/review/write';

		if (this.page_edit) {
			this.get_detail();
		}
	},
	mounted() {
		var vm = this;

		setTimeout(() => {
			$('.dropify').dropify();
		},500)
	}
})
</script>

