<script>
var pe_video_upload = Vue.component('pe_video_upload', {
    template: '#pe_video_upload_tem',
    name: 'pe_video_upload',
    components:['cpt_titlebar','cpt_vod_upload','cpt_loading'],
    props:['login_user_info'],
    computed:{
        isLogin(){
            return this.login_user_info.no > 0;
        },
        user_key(){
            return this.login_user_info.user_key;
        }
    },
    data(){
        return {
            upload_percent:0,
            init_load:false,
            title:'',
            content:'',
            upload_vod:'',
            upload_ing:false,
            upload_ing_msg : '',
            btn_text:'등록',
            page_edit:false,
            api_url:'',
            sub_title:'',
            vod_no:'',
            vod_id:'',
            vod_edit:{},
            thumb_url:'',
            upload_thumb:'',
            upload_chk:false,
            channel_data:[],
            channel_ban:false,

        }
    },
    methods:{
        cancel(){
            this.$router.go(-1);
        },
        editor_callback(text) {
            this.content = text;
        },
        vod_upload(){
            var vm = this;
            var fd = new FormData();
            this.upload_loading = true;

            if(!this.upload_chk){
                bootbox.alert('콘텐츠 업로드 이용 약관에 동의해주시기 바랍니다.');
                return
            }
            // value check
            if(!this.title){
                bootbox.alert('제목을 입력해주세요');
                return
            }else if(!this.page_edit && !document.getElementById('upload_vod').files[0]){
                bootbox.alert('영상을 업로드 해주세요');
                return
            }

            this.upload_ing = true;
            this.upload_ing_msg = '파일을 업로드 중입니다.';

            if(!this.page_edit){
                fd.append('vod_file', document.getElementById('upload_vod').files[0]);
            }

            const fds = new FormData($('#upload')[0]);
            if(this.login_user_info.type === 'seller'){
                fd.append('db_channel_no', this.login_user_info.channel_no);
                fd.append('db_product_no', fds.get('db_product_no'));
                fd.append('db_product_name', fds.get('db_product_name'));
            }

            fd.append('db_content', vm.content);
            fd.append('db_title' , vm.title);
            fd.append('db_sub_title' , vm.sub_title);
            fd.append('db_thumb_url' , vm.upload_thumb);
            fd.append('key', vm.user_key);
            fd.append('db_type', 'user');

            if(this.page_edit){
                fd.append('no', this.vod_no);
            }


            vm.upload_percent = 0;

            $.ajax({
                type: 'POST',
                url: this.api_url,
                data: fd,
                processData: false,
                contentType: false,
                dataType: 'json',

                xhr: function() { //XMLHttpRequest 재정의 가능
                    var xhr = $.ajaxSettings.xhr();
                    xhr.upload.onprogress = _.throttle(function(e) { //progress 이벤트 리스너 추가
                        vm.upload_percent = e.loaded * 100 / e.total;
                    }, 300);
                    return xhr;
                },
                success: function (data, status) {
                    if (data.code == 200) {
                        sgr_common.toast('영상이 업로드 되었습니다.');
                        vm.$router.replace('/video/my');
                    } else {
                        alert(data.message)
                    }
                    vm.upload_ing = false;
                    vm.upload_ing_msg = '';
                },
                error: function (request, status, error) {
                    sgr_common.toast('파일 업로드에 실패하였습니다.');
                    vm.upload_ing = false;
                    vm.upload_ing_msg = '';
                }
            });
        },

        ncp_image_uploader(file){
            var vm = this;
            data = new FormData();
            data.append("file", file.files[0]);
            data.append('field_name', 'file');
            data.append('file_dir', 'etc');

            $.ajax({
                data : data,
                type : "POST",
                url : "https://eatalk.live24.app/api/file/s3_img_upload",
                contentType : false,
                processData : false,
                success : function(data) {
                    if(data.code == 200){
                        vm.upload_thumb = data.result.filename;
                    }else{
                        sgr_common.toast(data.message);
                    }
                }
            });
        },

        get_detail(){
            var vm = this;

            $.ajax({
                type: 'GET',
                url: 'https://eatalk.live24.app/api/vod/json/detail',
                data:{
                    video_id : this.vod_id,
                    key:vm.user_key
                },
                dataType: 'json',
                success: function(res, status) {
                    if(res.code==200) {
                        vm.init_load = true;

                        vm.vod_no = res.result.data.no;
                        vm.title = res.result.data.title;
                        vm.sub_title = res.result.data.sub_title;
                        vm.content = res.result.data.content;
                        vm.upload_thumb = res.result.data.thumb_url;
                        vm.upload_vod = res.result.data.video_id+'.mp4';
                    }else{
	                    bootbox.alert(res.message);
                    }
                    setTimeout(()=>{
                        vm.v_product = true;
                    },1000);
                }
            });
        },

        getMyChannelData(){
            var vm = this;
            return new Promise(function(resolve, reject){
                $.ajax({
                    type: 'GET',
                    url: 'https://eatalk.live24.app/api/channel/json/my_detail',
                    data:{
                        key:vm.user_key
                    },
                    dataType: 'json',
                    success: function(res, status) {
                        if(res.code==200) {
                            //console.log(res)
                            vm.init_load = true;
                            vm.channel_data = res.result.data;
                            if(vm.channel_data.state !='Y'){
                                vm.channel_ban = true;
                            }
                            resolve();

                        }else{
                            //alert(res.message);
                            reject(new Error(res.message));
                        }

                        setTimeout(()=>{
                            vm.init_load_end = true
                        },300)
                    }
                });
            });
        },
    },
    created() {

        this.getMyChannelData().then(()=>{
            setTimeout(() => {
                $('.dropify').dropify();
            }, 300);

            this.page_edit = this.$route.params.type;
            if(this.page_edit){
                this.vod_id = this.$route.params.type;
            }

            this.api_url =  this.page_edit ? 'https://eatalk.live24.app/api/vod/json/edit' : 'https://eatalk.live24.app/api/vod/json/write'

            // 영상 수정
            if(this.page_edit){
                this.btn_text = '수정';
                this.get_detail();
            }
        })


    }

})
</script>

