	*
	*****************************************************************/
	function set_cache(key,res,time){
		if(!time){
			time = 60*60*24;
		}
		var cache = {
			key : key,
			res : res,
			time : time,
			date : new Date().getTime()
		};
		localStorage.setItem(key,JSON.stringify(cache));

	}

	/*****************************************************************
	*
	*  SGRSOFT Developer : now17
	*  Date : 2021/11/09
	*  Description : 캐시 가져오기
	*
	*****************************************************************/
	function get_cache(key){
		var cache = localStorage.getItem(key);
		if(cache){
			cache = JSON.parse(cache);
			if(cache.date + (cache.time*1000) > new Date().getTime()){
				return cache.res;
			}else{
				localStorage.removeItem(key);
				return false;
			}
		}else{
			return false;
		}
	}


