	function set_cache(key,res,time){
		if(!time){
			time = 60*60*24;
		}
		var cache = {
			key : key,
			res : res,
			time : time,
			date : new Date().getTime()
		};
		try{
			localStorage.setItem(key, JSON.stringify(cache));
		}catch(e){

		}

	}

